Hop.create!([
  {name: "Agnus", origin: "Czech Republic", alpha: 10.5, hop_type: "Bittering", beta: 5.25, description: "Several large Czech breweries like Agnus as a bittering hop because its alpha/beta ratio is relatively high, which they believe makes their beer more stable. High geraniol level, oils indicate dry hopping potential.\n            Used for: Ales\n            Aroma: Strong hop aroma, spicy\n            Substitutions: Unknown\n            Storage: Unknown\n            9-15% AA / 4-8% Beta", global: true, user_id: 1, aromas: nil},
  {name: "Amarillo Gold", origin: "U.S.", alpha: 8.5, hop_type: "Aroma", beta: 6.0, description: "Unknown origin, but character similar to Cascade.  Used for both bitterness and aroma.\n            Used for: IPAs, Ales\n            Aroma: Citrus, flowery\n            Substitutes: Cascade, Centennial", global: true, user_id: 1, aromas: nil},
  {name: "Apollo", origin: "U.S.", alpha: 17.0, hop_type: "Bittering", beta: 6.75, description: "Super high alpha variety from the Hopsteiner breeding program released in 2006.  High alpha and low cohumulone makes it a great bittering hop. Adds a strong grapefruit/hop note if added late in boil.\n            Used for: IPA, IIPA\n            Aroma: Strong grapefruit and hoppy notes\n            Substitutes: Nugget, CTZ, Bravo\n            Storage: Excellent/Very Good (80-90% AA remaining after 6 months at 20 C)\n            15-19% AA / 5.5-8% Beta", global: true, user_id: 1, aromas: nil},
  {name: "Aquila", origin: "U.S.", alpha: 6.5, hop_type: "Aroma", beta: 3.0, description: "Aroma hops developed in 1988.  Limited use due to high cohumolone.\n            Used for: Aroma hops\n            Substitutes: Cluster\n            No longer commercially grown.", global: true, user_id: 1, aromas: nil},
  {name: "Atlas", origin: "Slovenia", alpha: 9.0, hop_type: "Bittering", beta: 3.7, description: "A cross between Brewers Gold and Slovenian wild hops.  Primarily a bittering hop with pleasant bitterness and aroma.\n            Used for: Bittering ales, lagers\n            Substitutes: Brewers Gold", global: true, user_id: 1, aromas: nil},
  {name: "Atlas (Styrian Atlas)", origin: "Slovenia", alpha: 9.5, hop_type: "Bittering", beta: 3.7, description: "A cross between Brewers Gold and Slovenian wild hops.  Primarily a bittering hop with pleasant bitterness and aroma.\n            Used for: Bittering ales, lagers\n            Aroma: Fresh hoppy aroma\n            Substitutes: Brewers Gold\n            Storage: Very Good\n            7-11% AA / 3.2-41% Beta", global: true, user_id: 1, aromas: nil},
  {name: "Aurora (Super Styrian Aurora)", origin: "Slovenia", alpha: 8.0, hop_type: "Both", beta: 4.0, description: "Also called Super Styrian Aurora.  This is a hybrid between Northern Brewer and TG.  Pleasant hoppy aroma similar to Styrian Golding but lighter.\n            Used for: Strong Ale, Pale Ale, Bitter\n            Aroma: Intense and pleasant hoppy aroma\n            Substitutes: Styrian Golding\n            Storage: Excellent/Very Good (80-85% AA after 6 months at 20 C)\n            7-9% AA/ 3-5% Beta", global: true, user_id: 1, aromas: nil},
  {name: "Azacca", origin: "U.S.", alpha: 15.0, hop_type: "Both", beta: 2.0, description: "Notes: Dual purpose hop. Good for dry hopping\n            Aroma: Citrus, tropical, floral", global: true, user_id: 1, aromas: nil},
  {name: "Banner", origin: "U.S.", alpha: 10.0, hop_type: "Bittering", beta: 4.0, description: "Bittering hops developed in 1988.  Use largely discontinued due to poor storage.\n            Used for: U.S. style ales\n            Substitutes: Cluster", global: true, user_id: 1, aromas: nil},
  {name: "Belma", origin: "U.S.", alpha: 9.8, hop_type: "Both", beta: 0.0, description: "New variety produced at Puterbaugh Farms. First introduced to public Fall 2012.\n        Used for: Expedrimental Ales and Lagers\n        Aroma: A very clean hop, with a very orange, slight grapefruit, tropical (but not mango/guava, more like pineapple), strawberry, and melon\n        Substitutions: Unknown\n        Storage: Unknown\n        9.8% AA / ??% Beta (5 year avg not yet established)", global: true, user_id: 1, aromas: nil},
  {name: "Bobek (Styrian Golding B)", origin: "Slovenia", alpha: 5.25, hop_type: "Both", beta: 5.0, description: "Also called Styrian Golding B - a cross between Northern Brewer and a TG seedling.\n            Used for: English Ales, ESB, Lagers, Pilsners\n            Aroma: Intense and pleasant hoppy aroma\n            Substitutes: Northern Brewer,  Styrian Golding, UK or US Fuggle, Willamette\n            Storage: Unknown\n            3.5-7% AA / 4-6.1% Beta", global: true, user_id: 1, aromas: nil},
  {name: "Ahtanum", origin: "U.S.", alpha: 6.0, hop_type: "Aroma", beta: 5.25, description: "Distinctive aromatic hops with moderate bittering power from Washington.\n            Used for: American ales and lagers\n            Aroma: Distinctive floral and citrus aromas\n            Substitutes: Amarillo, Cascade", global: true, user_id: 1, aromas: "grapefruit, geranium"},
  {name: "Aramis", origin: "France", alpha: 8.1, hop_type: "Aroma", beta: 4.2, description: "Very fine spice and herbal notes with subtle citrus characteristics. Closely resembling Strisselspalt, but with higher oil and alpha content.\n\n            Used for: Belgian-style Ales\n            Substitutions: Willamette, Challenger, Ahtanum\n            Storage: Unknown", global: true, user_id: 1, aromas: "green, herbal, spicy, citrus"},
  {name: "Aurora", origin: "Slovenia", alpha: 8.25, hop_type: "Both", beta: 3.5, description: "Also called Super Styrian.  This is a hybrid between Northern Brewer and TG.  It has a pleasant hoppy aroma similar to Styrian Goldings but lighter.\n            Used for: Often mixed with other hops in lagers.\n            Substitutes: Styrian Goldings", global: true, user_id: 1, aromas: "noble"},
  {name: "Bitter Gold", origin: "U.S.", alpha: 17.0, hop_type: "Both", beta: 7.0, description: "Intense stone fruit especially pear and watermellon. A super-alpha variety with excellent aroma.\n\n            Used for: All Styles\n            Substitutions: Galena, Nugget", global: true, user_id: 1, aromas: "pear, watermelon, stone fruit, fresh cut grass"},
  {name: "Boadicea", origin: "United Kingdom", alpha: 8.5, hop_type: "Both", beta: 3.5, description: "Released in 2004. Excellent for finishing and dry hopping.\n            Used for: Ales\n            Aroma: Mild floral and spicy characteristics with slightly grassy overtones\n            Substitutions: Unknown\n            Storage: Unknown\n            7-10% AA / 3-4% Beta", global: true, user_id: 1, aromas: "floral, orchard blossom, ripe fruit"},
  {name: "Bobek", origin: "Slovenia", alpha: 5.25, hop_type: "Both", beta: 5.0, description: "Also called Styrian Golding B - a cross between Northern Brewer and a TG seedling.  Pleasant hop aroma and flavor.\n            Used for: Moderate English ales and lagers/Pilsners\n            Substitutes: Northern Brewer", global: true, user_id: 1, aromas: "noble"},
  {name: "Bramling Cross", origin: "United Kingdom", alpha: 6.0, hop_type: "Aroma", beta: 3.0, description: "Developed in 1927 from commercial Goldings and wild hop variety.\n            Used for: ESB, Bitters, Pale Ale\n            Aroma: Mild, fruity, currant aroma.\n            Substitutes: East Kent Goldings, Progress, Whitbred", global: true, user_id: 1, aromas: "blackcurrant, loganberry, vanilla"},
  {name: "Bravo", origin: "U.S.", alpha: 15.5, hop_type: "Bittering", beta: 3.5, description: "A second generation super high alpha hops from the Hopsteiner breeding program released in 2006.  Good bittering hop.\n            Aroma: Fruity and floral aroma.\n            Substitutes: Columbus, Zeus, Tomahawk", global: true, user_id: 1, aromas: "orange, vanilla"},
  {name: "Bullion", origin: "United Kingdom", alpha: 8.0, hop_type: "Bittering", beta: 4.5, description: "Used in strong dark English ales, some heavy German lagers.\n            Aroma: Strong, pungent and bitter in flavor.  Blackcurrant flavor.\n            Substitutes: Chinook, Eroica, Brewer&#39;s Gold", global: true, user_id: 1, aromas: nil},
  {name: "Calypso", origin: "U.S.", alpha: 13.0, hop_type: "Both", beta: 5.5, description: "A new, dual-purpose hop from the Hopsteiner breeding program that may establish itself as &quot;special&quot;.\n            Used for: Ales, Stouts and Barley Wines\n            Aroma: Not as tropical as the name might imply but rich in both stone fruits (pear and peaches, notes of cherry) and citrus.\n            Substitutions: Unknown\n            Storage: Good (65-70% AA after 6 months at 20 C)\n            12-14% AA / 5-6% Beta", global: true, user_id: 1, aromas: nil},
  {name: "Cascade, New Zealand", origin: "New Zealand", alpha: 7.0, hop_type: "Both", beta: 5.2, description: "Flowers, citrus &amp; spice with grapefruit the noticeable fragrance quite often. This medium aroma balances the low bittering value. Very popular hop among craft brewers.\n\n            Used for: Pale Ales, IPAs, Porters\n            Substitutions: Centennial, Amarillo", global: true, user_id: 1, aromas: nil},
  {name: "Chinook, New Zealand", origin: "New Zealand", alpha: 12.1, hop_type: "Both", beta: 7.6, description: "Piney with exotic spice and infused with grapefruit. This alluring aroma and a high bittering value has gained this hop full respect from craft &amp; major brewers.\n\n            Used for: American Pale Ale, IPA, Porter, Stout\n            Substitutions: Southern Cross, Sticklebract, Nugget, Northern Brewer", global: true, user_id: 1, aromas: nil},
  {name: "Brewer's Gold, UK", origin: "United Kingdom", alpha: 7.5, hop_type: "Bittering", beta: 3.7, description: "Brewers Gold is a complex bittering hop that is noted to have a sharp or pungent bittering quality. It also imparts a fruity yet spicy aroma as well as having a black currant characteristic. It is on the low end of the bittering hop scale at 8 to 10% AAU typically. Brewers Gold can be used in a wide range of styles from English Ales to German Lagers and adds a decidely &#39;European&#39; element to the beer. These make a good partner to noble varieties such as Tettnang and Hallertauer.\n\n            Used for: English Ale, German Lagers\n            Substitutions: Bullion, Chinook, Galena", global: true, user_id: 1, aromas: "blackcurrant, spicy"},
  {name: "Brooklyn", origin: "New Zealand", alpha: 18.0, hop_type: "Bittering", beta: 9.0, description: "A high alpha hop with distinctive tropical, grapefruit and passion fruit characters. Developed from Southern Cross.\n\n            Used for: IPAs\n            Substitutions: N/A", global: true, user_id: 1, aromas: "grapefruit, passionfruit, spice, sweet hay"},
  {name: "Bullion", origin: "U.S.", alpha: 7.5, hop_type: "Both", beta: 4.5, description: "A major hop variety since the mid-1940s, however, product ceased in 1985 due to super alpha hops with better storage stability. Commonly used in darker beers.\n            Used for: Stouts, ESB, Scotch Ale, IPA, Porter, Heavy German lagers.\n            Aroma: Strong, pungent and bitter in flavor. Intense Black currant aroma.\n            Substitutes: Chinook, Galena, Eroica, Columbus, Northern Brewer (German), Brewer&#39;s Gold (German).\n            Storage: Poor (40%-50% AA after 6 months at 20 C)\n            6.5-9% AA / 3.2-6% Beta", global: true, user_id: 1, aromas: "zesty, blackcurrant"},
  {name: "Cascade", origin: "U.S.", alpha: 5.5, hop_type: "Both", beta: 6.0, description: "A hops with Northern Brewers Heritage\n            Used for: American ales and lagers\n            Aroma: Strong spicy, floral, grapefruit character\n            Substitutes: Centennial\n            Examples: Sierra Nevada Pale Ale, Anchor Liberty Ale", global: true, user_id: 1, aromas: "grapefruit, floral"},
  {name: "Cashmere", origin: "U.S.", alpha: 8.5, hop_type: "Both", beta: 6.7, description: "Daughter of Cascade. Mild herbal aroma with strong melon, lemon, and slightly spicy notes.\n\n            Used for: American Pale Ale, IPA\n            Substitutions: N/A", global: true, user_id: 1, aromas: "melon, lemon, lime, spice"},
  {name: "Celeia", origin: "Slovenia", alpha: 4.5, hop_type: "Both", beta: 2.6, description: "A dual purpose hops that is a hybrid between Savinjski Golding, Aurora and Slovenian wild hops.  Similar in aroma to Saaz.\n            Used for: Light lagers, Pilsners, Belgian ales.\n            Substitutes: Saaz, Styrian Goldings", global: true, user_id: 1, aromas: "noble"},
  {name: "Centennial", origin: "U.S.", alpha: 10.0, hop_type: "Bittering", beta: 4.0, description: "Used for: General purpose bittering, aroma in American ales and Wheats\n            Aroma: Floral, citrus aroma, spicy, clean bittering flavor\n            Substitutes: Galena, Eroica, Nugget, Bullion\n            Examples: Sierra Nevada Celebration Ale, Sierra Nevada Bigfoot", global: true, user_id: 1, aromas: "lemon, floral"},
  {name: "Challenger", origin: "United Kingdom", alpha: 7.5, hop_type: "Aroma", beta: 4.0, description: "Variant of Northern Brewers heritage\n            Used for: Both aroma and bittering for ales and lagers\n            Aroma: Fruity in flavor, spicy aroma, blends well\n            Substitutes: N/A", global: true, user_id: 1, aromas: "cedar, green tea, sweet fruit"},
  {name: "Chelan", origin: "U.S.", alpha: 13.25, hop_type: "Bittering", beta: 9.25, description: "High alpha variety from the Haas breeding program released in 2004.  It is a variant of Galena and very similar in profile to Galena.\n            Aroma: Pleasant citrus notes\n            Substitutes: Galena", global: true, user_id: 1, aromas: "mild"},
  {name: "Chinook", origin: "U.S.", alpha: 13.0, hop_type: "Bittering", beta: 3.6, description: "Strong versatile bittering hop\n            Used for: Ales, porters, stouts\n            Aroma: Heavy and spicy aroma\n            Substitutes: Galena, Eroica, Nugget, Bullion\n            Examples: Sierra Nevada Stout, Sierra Nevada Celebration Ale", global: true, user_id: 1, aromas: "grapefruit, spicy, pine"},
  {name: "Citra", origin: "U.S.", alpha: 12.0, hop_type: "Both", beta: 4.0, description: "Special aroma hops released in 2007.  Imparts high alpha/oil content but low cohumulone.\n            Aroma: Adds interesting citrus and tropical fruit character to the beer.\n            Substitutes: Unknown", global: true, user_id: 1, aromas: "grapefruit, melon, lime, gooseberry, passion fruit"},
  {name: "Cluster", origin: "U.S.", alpha: 7.0, hop_type: "Bittering", beta: 4.8, description: "Used for: General purpose bittering hop\n            Aroma: Floral, slightly spicy. Sharp aroma and sharp flavor\n            Substitutes: Eroica, Galena", global: true, user_id: 1, aromas: "floral, earthy, sweet fruit"},
  {name: "Columbia", origin: "United Kingdom", alpha: 5.5, hop_type: "Bittering", beta: 3.0, description: "Sibling of Williamette hops.\n            Used for: All English Ales\n            Aroma: Close to Fuggles\n            Substitutes: Fuggles, Williamette", global: true, user_id: 1, aromas: "fuggle type"},
  {name: "Brewer's Gold, Germany", origin: "Germany", alpha: 7.0, hop_type: "Bittering", beta: 3.0, description: "Black currant, fruit and spice characteristics. Has a high resin content and gives a well-balanced bitterness which is ideally complimented by late hop additions in lager beers.\n\n            Usage: Ale, Bi&egrave;re de Garde, Lambic, Lager, Saison\n            Substitutions: Galena, Northdown, Northern Brewer\n            Storage: Unknown", global: true, user_id: 1, aromas: "blackcurrant, spicy"},
  {name: "Columbus/Tomahawk/Zeus (CTZ)", origin: "U.S.", alpha: 15.5, hop_type: "Bittering", beta: 4.5, description: "Engineered Centennial Substitute - High alpha bittering hops.\n            Used for: IPA, American Pale Ale, Stout, Lager, Barley Wine\n            Aroma: Pungent, spicy. Strongly aromatic, but clean tasting bitterness\n            Substitutes: Other CTZ, Galena, Chinook, Nugget, Target (U.K.), Northern Brewer (Germany)\n            Storage: Poor/Very Poor (45-65% AA after 6 months at 20 C)\n            14-16.5% AA / 4-5% Beta", global: true, user_id: 1, aromas: nil},
  {name: "Delta", origin: "U.S.", alpha: 6.0, hop_type: "Bittering", beta: 6.0, description: "A backcross between Fuggles and Cascade, recently released (2012) by the Hopsteiner breeding program. Woody and herbal with a citrus kick. Not as assertive as dual purpose hops but adds distinctive notes in a blend.\n            Used for: IPA, Pale Ale\n            Aroma: Mild and pleasant aroma; slightly spicy with a hint of citrus.\n            Substitutions: Fuggle, Willamette\n            Storage: Excellent (80-90% AA after 6 months at 20 C)\n            5-7% AA / 5-7% Beta", global: true, user_id: 1, aromas: nil},
  {name: "El Dorado", origin: "U.S.", alpha: 15.0, hop_type: "Both", beta: 7.5, description: "Bred by CLS Farms, LLC, available on a larger scale for the first time during 2012 harvest. Fits the dual purpose/special mold.\n            Used for: Experimental Ales\n            Aroma: Tropical fruit flavors, intense aromas of stone fruits (pear, cherries), candy (Lifesavers), watermelon.\n            Substitutions: None at this time\n            Storage: Good\n            14-16% AA / 7-8% Beta", global: true, user_id: 1, aromas: nil},
  {name: "Equinox (HBC 366)", origin: "U.S.", alpha: 15.0, hop_type: "Aroma", beta: 4.8, description: "A pronounced aroma profile with citrus, tropical fruit, floral and herbal characteristics. Specific descriptors include lemon, lime, papaya, apple, and green pepper.\n\n            Used for: Experimental Ale\n            Aroma: Exceptional aroma profile includes notes of citrus, tropical and pine\n            Substitutions: Unknown\n            Storage: Unknown\n            14.4-15.6% AA / 4.6-5.1% Beta", global: true, user_id: 1, aromas: nil},
  {name: "Eureka!", origin: "U.S.", alpha: 18.0, hop_type: "Both", beta: 2.0, description: "Notes: Offspring of Apollo and Merkur. Similar to Simcoe. Dual purpose. Good for dry hoping.\n\n            Aroma: Herbal, pine, mint, grapefruit rind, tangerine", global: true, user_id: 1, aromas: nil},
  {name: "Feux Coeur Francais", origin: "Australia", alpha: 14.0, hop_type: "Bittering", beta: 6.0, description: "Rare, high alpha hops from Australia with genetic roots in the Burgundy region of France.  It was adopted to the Victoria region of Australia and introduced in 2010.", global: true, user_id: 1, aromas: nil},
  {name: "Fuggle, New Zealand", origin: "New Zealand", alpha: 6.1, hop_type: "Aroma", beta: 3.0, description: "Fuggle is the classic English aroma hop and is often used in conjunction with Goldings. It can be used as a main boiling hop to impart a mild, soft grassy/woody floral aroma and is also used for dry hopping.\n\n            Used for: English Ales, Porter, Stout, English IPA\n            Substitutions: UK Fuggle, NZ Willamette, NZ Styrian Golding", global: true, user_id: 1, aromas: nil},
  {name: "Fuggle, Organic", origin: "United Kingdom", alpha: 4.0, hop_type: "Aroma", beta: 2.0, description: "As Fuggle (U.K.), only organic variety\n            Typical alpha: 3-5%. Organically grown Fuggle hops from the UK. Wonderful earthy, pipe-tobacco, floral character. Classic hop for any English ale. ", global: true, user_id: 1, aromas: nil},
  {name: "Palisade", origin: "U.S.", alpha: 7.5, hop_type: "Both", beta: 7.0, description: "Also called YCR-4, Palisade is an aroma variety bread by Yakima Chief Ranches.  It has both aromatic and moderate bittering properties.\n            Substitutes: Willamette", global: true, user_id: 1, aromas: nil},
  {name: "Falconer's Flight", origin: "U.S.", alpha: 11.0, hop_type: "Both", beta: 2.0, description: "Proprietary pellet blend comprised of many of the Northwest&rsquo;s most unique hop varieties and is perfect for any Northwest-style IPA.\n            Used for: IPA, Pale Ale, Experimental Ale\n            Aroma: distinct tropical, citrus, floral, lemon and grapefruit tones.\n            Substitutions: Unknown (Citra, Simcoe, Sorachi Ace blend?)\n            Storage: Unknown\n            10-12% AA / 4.4% Beta", global: true, user_id: 1, aromas: nil},
  {name: "Comet", origin: "U.S.", alpha: 9.5, hop_type: "Bittering", beta: 4.0, description: "Very bitter hops - use sparingly with other hops\n            Used for: Bittering only\n            Substitutes: N/A", global: true, user_id: 1, aromas: "wild american, grass, grapefruit"},
  {name: "Crystal", origin: "U.S.", alpha: 3.5, hop_type: "Aroma", beta: 5.2, description: "Used for: Aroma and finishing hops for Lagers\n            Aroma: Clean, mild, pleasant and slightly spicy\n            Substitutes: Hallertauer, Mount Liberty", global: true, user_id: 1, aromas: "woody, green"},
  {name: "Dr. Rudi", origin: "New Zealand", alpha: 11.0, hop_type: "Both", beta: 8.0, description: "A dual purpose / bittering hop with classic resinous pine and lifted citrus characters.\n\n            Used for: Ales\n            Substitutions: N/A", global: true, user_id: 1, aromas: "resin, pine, lemongrass"},
  {name: "Ella (aka Stella)", origin: "Australia", alpha: 15.0, hop_type: "Both", beta: 5.9, description: "New name after a 2013 harvest in Australia (due to name infringement). Despite high alpha, sold as an aroma variety. When used in dry hopping or higher quantities, grapefruit and tropical characters come to the fore. Character obtained at lower rates can be best appreciated in lagers or pilsners, while the grapefruit and tropical flavors can be used to offset robust malt and yeast characters\n            Used for: Ales, Lagers, Pilsners\n            Aroma: tropical, subtle hints of star anise, sweet and floral, with lemon, grapefruit, apricot, and melon.\n            Substitutions: Galaxy\n            Storage: Unknown\n            13.3-16.3% AA / 4-7.8% Beta", global: true, user_id: 1, aromas: "floral, noble, spicy, star anise, grapefruit"},
  {name: "Eroica", origin: "U.S.", alpha: 13.0, hop_type: "Bittering", beta: 4.8, description: "Used for: General purpose bittering for ales, porters, stouts\n            Aroma: Clean bittering hop, very bitter\n            Substitutes: Galena, Northern Brewer, Chinook\n            Examples: Blackhook Porter, Ballard Bitter", global: true, user_id: 1, aromas: "fruity"},
  {name: "First Gold", origin: "United Kingdom", alpha: 7.5, hop_type: "Both", beta: 3.5, description: "Dwarf hop with English Golding character\n            Used for: Ales, ESB\n            Aroma: Similar to other Golding varieties, spicy\n            Substitutes: East Kent Goldings, Crystal", global: true, user_id: 1, aromas: "geranium, magnolia, apricot, tangerine, orange marmalade"},
  {name: "Fuggle", origin: "United Kingdom", alpha: 4.5, hop_type: "Aroma", beta: 2.5, description: "Traditional English-type aroma hop.\n            Used for: English Ales, ESB, Bitter, Lagers\n            Aroma: Mild, soft, fruity, spicy, woody\n            Substitute: Fuggle (U.S.), Willamette, Styrian Golding\n            Storage: Good/Very Good (70-80% AA after 6 months at 20 C)\n            3-5.6% AA 2-3% Beta", global: true, user_id: 1, aromas: "green tea, mint, grass, floral"},
  {name: "German Tradition", origin: "Germany", alpha: 6.0, hop_type: "Bittering", beta: 4.5, description: "Close descendant of Hallertauer Mittelfrueh bred for high yield.\n            Used for: Lager, Pilsner, Bock, some Weizens\n            Aroma: Medium intense floral and herbal (grassy) tones\n            Substitutes: Hallertau Mittelfruh, Liberty, Hersbrucker\n            Storage: Unknown\n            5-7% AA / 4-5% Beta", global: true, user_id: 1, aromas: nil},
  {name: "Golding, U.S.", origin: "U.S.", alpha: 5.0, hop_type: "Aroma", beta: 2.5, description: "Mild and delicate with sweet floral characteristics. Bred in the US from east Kent Golding. Golding hops consist of a group of traditional English aroma varieties which have been cultivated since 1790. Several\n            selections now exist including Cobbs, Early Bird, East Well, Bramling, Canterbury and Mathon, some of which are now being grown in the U. S. All Goldings are recognized as having a most typical English aroma.\n\n            Used for: Bitter\n            Substitutions: Fuggle, Willamette, UK East Kent Golding, Styrian Golding", global: true, user_id: 1, aromas: nil},
  {name: "Goldings, B.C.", origin: "Canada", alpha: 5.0, hop_type: "Aroma", beta: 3.2, description: "Used for: Bittering and finishing British ales, bitters, porters and stouts.\n            Aroma: Spicy, floral, rounded mild aroma.\n            Substitutes: East Kent Goldings, Fuggles", global: true, user_id: 1, aromas: nil},
  {name: "Greenburg", origin: "U.S.", alpha: 5.2, hop_type: "Aroma", beta: 7.2, description: "American hop variety from southern Idaho.  Fruity flavor with a touch of wood flavor.  Popular with microbreweries.", global: true, user_id: 1, aromas: nil},
  {name: "HBC 291 (Experimental)", origin: "U.S.", alpha: 12.1, hop_type: "Bittering", beta: 5.6, description: "As this is an experimental variety, there is no guarantee this hop will come to market en masse. Data obtained from HopUnion\n            Uses: Unknown\n            Aroma: Typically described as peppery, floral and fruity\n            Substitutions: Unknown\n            Storage: Unknown\n            11.2-13.1% AA / 5.1-6.1% Beta", global: true, user_id: 1, aromas: nil},
  {name: "HBC 342 (Experimental)", origin: "U.S.", alpha: 5.0, hop_type: "Bittering", beta: 2.0, description: "As this is an experimental variety, there is no guarantee this hop will come to market en masse. Data obtained from HopUnion. &quot;High alpha variety with a pleasant aroma&quot;\n            Uses: Pale Ale\n            Aroma: Mild citrus, tropical fruit and melon notes\n            Substitutions: Unknown (Belma or Citra?)\n            Storage: Unknown\n            ??% AA / ??% Beta", global: true, user_id: 1, aromas: nil},
  {name: "HBC 366 (Experimental)", origin: "U.S.", alpha: 15.0, hop_type: "Aroma", beta: 4.8, description: "As this is an experimental variety, there is no guarantee this hop will come to market en masse. Data obtained from HopUnion. &quot;The fantastic aroma combined with the high high oils make this hop very unique&quot;\n            Uses: Experimental Ale\n            Aroma: Exceptional aroma profile includes notes of citrus, tropical and pine\n            Substitutions: Unknown\n            Storage: Unknown\n            14.4-15.6% AA / 4.6-5.1% Beta", global: true, user_id: 1, aromas: nil},
  {name: "Hallertau Magnum", origin: "Germany", alpha: 14.0, hop_type: "Bittering", beta: 6.5, description: "German Hallertauer hybrid, widely used in Germany. Extremely appropriate for early boil additions.\n            Used for: German ales and lagers, bitters, IPAs, pilsners, pale ales, stouts.\n            Aroma: somewhat unnoticeable aroma; stable spicy flavor\n            Substitutes: Hallertau Taurus, Columbus, Nugget\n            Storage: Excellent (80%-85% AA after 6 months at 20 C)\n            12-14% AA / 4.5-5.5% Beta", global: true, user_id: 1, aromas: nil},
  {name: "Pekko", origin: "U.S.", alpha: 15.0, hop_type: "Both", beta: 2.0, description: "Notes: Any style that coud incorporate Saaz could use Pekko.\n\n            Aroma: Floral, mint, herbal, citrus", global: true, user_id: 1, aromas: nil},
  {name: "Gesho Kitel", origin: "Ethiopia", alpha: 5.0, hop_type: "Aroma", beta: 2.0, description: "Brundo's aroma hops Gesho stems, gathered from the highlands of Ethiopia, are sun-dried and packed fresh. These aromatic sticks and stem flavor centuries old traditional brewing recipes for beer and Ethiopian honey wine. Acid content unknown.", global: true, user_id: 1, aromas: nil},
  {name: "Goldings, East Kent", origin: "United Kingdom", alpha: 5.0, hop_type: "Aroma", beta: 3.5, description: "Used for: General purpose hops for bittering/finishing all British Ales\n            Aroma: Floral, aromatic, earthy, slightly sweet spicy flavor\n            Substitutes: Fuggles, BC Goldings\n            Examples: Bass Pale Ale, Fullers ESB, Samual Smith's Pale Ale", global: true, user_id: 1, aromas: nil},
  {name: "Fuggle, U.S.", origin: "U.S.", alpha: 4.75, hop_type: "Aroma", beta: 1.7, description: "This classic English aroma variety has long been grown in both Oregon and Washington. It has a typical English aroma and contributes a balanced bitterness. Fuggle is very suitable for English and American-style Ales. Mild wood and fruit characteristics.\n\n            Used for: English Ales\n            Substitutions: Willamette, UK Fuggle, Styrian Golding", global: true, user_id: 1, aromas: "mild, wood, grass, mint"},
  {name: "Galaxy", origin: "Australia", alpha: 14.0, hop_type: "Bittering", beta: 5.9, description: "A high alpha, dual purpose seedless hops with a unique hop aroma.  It can be used either for bittering or as a late hop addition to provide a flavor of citrus and passionfruit to the finished beer.\n            Aroma: Intense, pleasant, unique.", global: true, user_id: 1, aromas: "passionfruit, peach, clean citrus"},
  {name: "Galena", origin: "U.S.", alpha: 12.5, hop_type: "Bittering", beta: 8.0, description: "Used for: General bittering hops for all beers\n            Aroma: Strong, clean, balanced bittering\n            Substitutes: Eroica, Northern Brewer, Cluster, Chinook\n            Examples: Catamount Porter", global: true, user_id: 1, aromas: "sweet, pear, pineapple, lime, blackcurrant, grapefruit"},
  {name: "Glacier", origin: "U.S.", alpha: 5.6, hop_type: "Aroma", beta: 7.6, description: "Genetic mix of at least 8 hops including Elsasser, Brewers Gold, N Brewer, Bullion, Early Green, and others...released in 2000.\n            Used for: Aroma\n            Aroma: Excellent, hoppy\n            Substitutes: N/A", global: true, user_id: 1, aromas: "plum, blackberry, wood"},
  {name: "Golding, New Zealand", origin: "New Zealand", alpha: 4.2, hop_type: "Aroma", beta: 4.5, description: "Mild, delicate sweet floral character\n\n            Used for: English Ales\n            Substitutions: NZ Willamette, NZ Styrian Golding, NZ Fuggle, UK East Kent Golding", global: true, user_id: 1, aromas: "delicate, sweet floral"},
  {name: "Hallertau", origin: "U.S.", alpha: 4.5, hop_type: "Aroma", beta: 4.5, description: "This traditional German land-race variety has limited acreage in the U.S.\n            Used for: lager, pilsner, bock, wheat, Munich Helles, or Belgian style.\n            Aroma: considered mild, pleasant, and slightly flowery &amp; spicy.\n            Substitutions: Liberty, German Hallertau, German Tradition\n            Storage: Fair (50-60% AA after 6 months at 20 C)\n            3.5-5.5% AA / 3.5-5.5% Beta", global: true, user_id: 1, aromas: "noble"},
  {name: "Hallertau Blanc", origin: "Germany", alpha: 10.5, hop_type: "Aroma", beta: 5.0, description: "Floral and fruity with passion fruit, grapefruit, pineapple, grape and lemongrass overtones. One of several new German varieties with bold flavor profiles, perfect for American-style Ales.\n\n            Used for: American style ale\n            Substitutions: N/A\n            Storage Unknown", global: true, user_id: 1, aromas: "passion fruit, grapefruit, pineapple, grape, lemongrass"},
  {name: "Hallertauer", origin: "Germany", alpha: 4.8, hop_type: "Aroma", beta: 4.0, description: "Used for: German Ales, German/US/Canadian Lagers, Wheat Beers\n            Aroma: Pleasant, mild spicy flavor, clean, neutral flavor\n            Substitutes: Crystal, Liberty, Hallertauer Mittelfrueh", global: true, user_id: 1, aromas: nil},
  {name: "Hallertauer Hersbrucker", origin: "Germany", alpha: 4.0, hop_type: "Aroma", beta: 6.0, description: "Used for: German style lagers and wheat beers\n            Aroma: Noble, pleasant, clean flavor\n            Substitutes: Mt. Hood, Liberty, Hallertauer, Mittelfrueh\n            Examples: Wheathook Wheaten Ale", global: true, user_id: 1, aromas: nil},
  {name: "Hallertauer, New Zealand", origin: "New Zealand", alpha: 8.5, hop_type: "Both", beta: 3.0, description: "Hallertauer variant with classic aroma and ability to retain clean taste. Bred from German Hallertauer Mittelfrueh\n            Used for: Australian Ales\n            Substitutes: Hallertauer Mittelfrueh", global: true, user_id: 1, aromas: nil},
  {name: "Harmonie", origin: "Czech Republic", alpha: 6.5, hop_type: "Bittering", beta: 6.5, description: "With typical Czech aroma, Harmonie&#39;s finely balanced bitterness &quot;persists&quot; slightly longer than Saaz. Bohemie, released in 2010, is a similar Czech hop with traditional aroma.\n            5-8% AA / 5-8% Beta", global: true, user_id: 1, aromas: nil},
  {name: "HopShot", origin: "", alpha: 65.0, hop_type: "Both", beta: 2.0, description: "CO2-extracted hop resin that can be used for bittering or late additions to boiling wort. One milliliter of HopShot yields approximately 10 IBUs in 5 gallons of 1.050 wort when boiled for 60 minutes.\n            http://www.northernbrewer.com/documentation/hopshot.pdf", global: true, user_id: 1, aromas: nil},
  {name: "Idaho #7", origin: "U.S.", alpha: 13.0, hop_type: "Both", beta: 2.0, description: "Notes: Dual purpose, high alpha acid\n\n            Aroma: Tropical fruit, orange, apricot, pine.", global: true, user_id: 1, aromas: nil},
  {name: "Jarrylo", origin: "U.S.", alpha: 16.0, hop_type: "Both", beta: 2.0, description: "Notes: Underwhelming hop. Good if used in combination of hops with stronger flavors like Amarillo.\n\n            Aroma: Banana, pear, spice.", global: true, user_id: 1, aromas: nil},
  {name: "Lemondrop", origin: "U.S.", alpha: 6.0, hop_type: "Aroma", beta: 2.0, description: "Notes: Good for citrusy IPA, APA, IPL, or Saisons. Should be used with a hop with more distinct flavor that would benefit from the lemon flavor.\n\n            Aroma: Lemony, floral, earthy, herbal, grassy, pine, pepper", global: true, user_id: 1, aromas: nil},
  {name: "Liberty, New Zealand", origin: "New Zealand", alpha: 5.9, hop_type: "Both", beta: 4.7, description: "Mild and spicy, subtle lemon and citrus. Used in many styles. Contributes good bitterness in the beginning of the boil and an aroma of grapes, peaches, and vanilla at the end of the boil. Derived from Hallertau Mittlefruh.", global: true, user_id: 1, aromas: nil},
  {name: "Lublin", origin: "Poland", alpha: 5.0, hop_type: "Bittering", beta: 3.0, description: "Version of Saaz grown in Poland - also called &quot;Lubelski&quot;\n            Used for: Bohemian lagers and Pilsners\n            Aroma: Noble, mild flavor similar to Saaz\n            Substitutes: Saaz, Tettnanger", global: true, user_id: 1, aromas: nil},
  {name: "Lublin (Lubelski)", origin: "Poland", alpha: 4.0, hop_type: "Aroma", beta: 3.0, description: "Lublin, also called &quot;Lubelski&quot;, was bred from Saaz and carries with it many of the same characteristics.\n            Used for: Bohemian lagers and Pilsners\n            Aroma: Noble, mild flavor similar to Saaz\n            Substitutes: Saaz, Tettnanger, Sterling\n            Storage: Good (70%-75% AA after 6 months at 20 C)\n            3-5% AA / 2.5-4% Beta", global: true, user_id: 1, aromas: nil},
  {name: "Hallertauer Mittelfrueh", origin: "Germany", alpha: 4.0, hop_type: "Aroma", beta: 4.6, description: "Used for: Continental Lagers, German Ales/Lagers, US Lagers\n            Aroma: Pleasant, assertive, mild herbal aroma\n            Substitutes: Hallertauer, Mt. Hood, Liberty, Crystal\n            Examples: Sam Adams Boston Lager, Sam Adams Boston Lightship", global: true, user_id: 1, aromas: "grass, tea, citrus"},
  {name: "Helga (Southern Hallertau)", origin: "Australia", alpha: 5.5, hop_type: "Bittering", beta: 4.4, description: "Perviously known as Southern Hallertau. Bears almost no aroma similarities to it&#39;s European cousin. Very unique aroma with moderate bittering.\n            Used for: Austrialian and English-style ales.\n            Aroma: Ddelicate floral and subtle herbal aroma\n            Substitutes: Unknown (possible Nobel Hop variety)\n            Storage: Unknown\n            5-6% AA / 3.8-5.4% Beta", global: true, user_id: 1, aromas: "pleasant, noble"},
  {name: "Herald", origin: "United Kingdom", alpha: 12.0, hop_type: "Bittering", beta: 5.2, description: "Dwarf variety bred at Wye College (mid 1990s)\n            Used for: Bittering of ales\n            Aroma: Strong, acceptable\n            Substitutes: High alpha English bittering hops", global: true, user_id: 1, aromas: "orange, grapefruit"},
  {name: "Herkules", origin: "Germany", alpha: 14.5, hop_type: "Bittering", beta: 2.0, description: "A German high alpha cross variety, bred in Huell and released in 2005. Imparts robust, hoppy tang flavors.\n            Used for: hoppy German-style ales or lagers\n            Aroma: Spicy floral and hop notes, with hints of pine, lemon and black pepper.\n            Substitutions: Unknown\n            Storage: Good\n            13-17% AA / 4-5.5% Beta", global: true, user_id: 1, aromas: "robust, melon"},
  {name: "Horizon", origin: "U.S.", alpha: 12.0, hop_type: "Bittering", beta: 7.5, description: "Good dual purpose hops derived from Nugget hops in Oregon (1970)\n            Used for: All purpose Ales, Lagers\n            Aroma: Pleasant, hoppy\n            Substitutes: Magnum or other high alpha hops", global: true, user_id: 1, aromas: "floral bouquet, spicy"},
  {name: "Huell Melon", origin: "Germany", alpha: 7.2, hop_type: "Aroma", beta: 7.5, description: "Distinct fruit characteristics including honeydew melon and strawberry flavors. An excellent consideration for varieties not necessarily associated with typical hop flavors.\n\n            Substitutions: N/A\n            Storage Unkonwn", global: true, user_id: 1, aromas: "honeydew melon, orange, strawberry, tropical fruit"},
  {name: "Kohatu", origin: "New Zealand", alpha: 6.5, hop_type: "Aroma", beta: 5.5, description: "Moderate aroma with lime and pineapple with traces of pine. Overuse can exhibit &quot;dank&quot; character.\n\n            Used for: Belgian Ales, Wheat Beer, Blonde Ale\n            Substitutions: N/A", global: true, user_id: 1, aromas: "tropical fruit"},
  {name: "Liberty", origin: "U.S.", alpha: 4.3, hop_type: "Aroma", beta: 3.5, description: "Used for: German style lagers\n            Aroma: Fine, mild, slightly spicy flavor\n            Substitutes: Hallertau, Mt Hood, Crystal\n            Examples: Pete&#39;s Wicked Lager", global: true, user_id: 1, aromas: "noble, floral, herbal, spicy, delicate, lemon"},
  {name: "Magnum", origin: "U.S.", alpha: 12.0, hop_type: "Bittering", beta: 5.25, description: "High-alpha hop with clean, neutral bitterness. Can be used in almost any recipe.\n            Used for: Ale, Lager, Stout, hefeweizen, IPA, Bitter\n            Aroma: No distinctive aroma characteristics\n            Substitutions: German Magnum, CTZ, Nugget\n            Storage: Excellent/Very good (80%-85% AA after 6 months at 20 C)\n            10-14% AA / 4.5-7% Beta", global: true, user_id: 1, aromas: "apple, pepper"},
  {name: "Marynka", origin: "Poland", alpha: 10.5, hop_type: "Bittering", beta: 11.4, description: "Bittering hops with high aroma.  Primarily for use in Pilsner, lagers, ales or wheats.  Comparable to English Goldings or Chinook.\n            Used for: Lager, ales\n            Substitutes: Chinook, English Golding", global: true, user_id: 1, aromas: nil},
  {name: "Merkur (Hallertauer Merkur)", origin: "Germany", alpha: 14.0, hop_type: "Bittering", beta: 5.1, description: "Sometimes called Hallertauer Merkur, this variety was bred from a cross between Hallertaur Magnum and variety 81/8/13 from the Hull Hops Research Institute.  Mostly used for bittering qualities, appropriate for early to mid additions.\n            Aroma: Strong earthy, floral and citrus impressions\n            Substitutions: Maganum (U.S.), Hallertau Magnum\n            Storage: Fair/Good (60%-70% AA after 6 months at 20 C)\n            12-16.2% AA / 5.1% Beta", global: true, user_id: 1, aromas: nil},
  {name: "Pacific Hallertau (aka Pacifica)", origin: "New Zealand", alpha: 5.5, hop_type: "Aroma", beta: 5.8, description: "Hallertau variant which produces a typical Mittelfruh aroma combined with smooth bouquet and mild buttery flavor notes; typical of the flavor achieved in classic European lagers\n            Used for: Australian Ales, European lager\n            Aroma: Clean, floral with slight cinnamon-like spiciness. Orange marmalade and citrus aroma notes when used in late addition.  Soft finish if used in bittering.\n            Substitutes: Chinook, Hallertauer Mittelfruh, Liberty, Mt. Hood\n            Storage: Good/Very Good\n            5-6% AA / 5.5-6.2% Beta", global: true, user_id: 1, aromas: nil},
  {name: "Millenium", origin: "U.S.", alpha: 14.25, hop_type: "Bittering", beta: 5.7, description: "Originally developed as part of the Haas breeding program in the US, Millenium entered production in 2000.  It is a very high alpha hops used for bittering.\n            Aroma: Mild, herbal\n            Substitutes: Columbus, Tomahawk, Zeus and Nugget", global: true, user_id: 1, aromas: "resin, herbal, floral, pear"},
  {name: "Mosaic (HBC 369)", origin: "U.S.", alpha: 12.25, hop_type: "Both", beta: 3.5, description: "Available in quantity for the first time after 2012 harvest. Still known to many as HBC 369. A daughter of Simcoe crossed with a Nugget derived male. An excellent aroma variety.\n            Used for: Ales, IPA, Experimental Ales\n            Aroma: Rich in mango, lemon, citrus, pine, and notably, blueberry.\n            Substitutes: Unknown\n            Storage: Good (25% AA after 6 months at 20 C)\n            11-13.5% AA / 3.2-3.9% Beta", global: true, user_id: 1, aromas: "blueberry, tangerine, papaya, blossoms, rose, bubble gum"},
  {name: "Motueka", origin: "New Zealand", alpha: 7.0, hop_type: "Aroma", beta: 5.25, description: "Flexible hop developed from Saaz.  Has noble character and aroma, lemon and lime tones, balanced bitterness, and unique flavor.\n\n            Used for: Lagers, Belgian Ales and Bohemian Pilsner.\n            Substitutions: Saaz, Sterling", global: true, user_id: 1, aromas: "lemon, lime, tropical fruit"},
  {name: "Mt. Hood", origin: "U.S.", alpha: 6.0, hop_type: "Aroma", beta: 6.3, description: "Used for: European Lagers, finishing\n            Aroma: Mild with a clean aroma, neutral flavor. Somewhat pungent.\n            Substitutes: Hallertauer, Liberty, Crystal", global: true, user_id: 1, aromas: "pungent"},
  {name: "Mt. Rainier", origin: "U.S.", alpha: 7.0, hop_type: "Both", beta: 2.0, description: "Complex parentage, including Hallertau, Galena, and Fuggle. HopUnion has stated this hop was only bred thru 2010.\n            Used for: Lager\n            Aroma: Excellent floral and noble aromas, with citrus and licorice tones.\n            Substitutions: Hallertau, Fuggle\n            Storage: Unknown\n            5-8.1% AA / 5-7% Beta", global: true, user_id: 1, aromas: "noble, licorice, floral"},
  {name: "Nelson Sauvin", origin: "New Zealand", alpha: 12.0, hop_type: "Bittering", beta: 7.0, description: "Triploid variety bred from &quot;Smoothcone&quot; released in 2000.  Oil profile said to taste like &quot;fresh crushed gooseberries&quot; similar to Sauvignon Blanc, giving it its name.  Very fruity, citrusy - use in moderation.\n            Used for: Pale ale, Super Premiums.\n            Substitutes: None", global: true, user_id: 1, aromas: "white wine, gooseberry, grape, sauvignon blanc"},
  {name: "Newport", origin: "U.S.", alpha: 13.5, hop_type: "Bittering", beta: 6.0, description: "High alpha hops released in 2002 as a potential replacement for Galena.  Note that these hops have a large alpha range of 9.8%-17% so check the package!  Fairly pungent.\n            Used for: Primarily used for bittering in ales, stouts, barley wine.\n            Substitutes: Galena", global: true, user_id: 1, aromas: "wine, balsamic"},
  {name: "Northdown", origin: "United Kingdom", alpha: 8.5, hop_type: "Both", beta: 5.5, description: "Variation of Northern Brewers hops grown in England.\n            Used for: Bittering in English ales\n            Aroma: Good bittering, flavor and aroma\n            Substitutes: Northern Brewer", global: true, user_id: 1, aromas: "floral, pine, cedar, berry"},
  {name: "Northern Brewer", origin: "Germany", alpha: 8.5, hop_type: "Both", beta: 4.0, description: "Also called Hallertauer Northern Brewers\n            Used for: Bittering and finishing both ales and lagers of all kinds\n            Aroma: Fine, dry, clean bittering hop.  Unique flavor.\n            Substitutes: Hallertauer Mittelfrueh, Hallertauer\n            Examples: Anchor Steam, Old Peculiar, ", global: true, user_id: 1, aromas: "mint, pine, grass"},
  {name: "Nugget", origin: "U.S.", alpha: 13.0, hop_type: "Bittering", beta: 5.0, description: "Used for: Bittering hops for ales\n        Aroma: Strong bittering, heavy, herbal aroma, spicy\n        Substitutes: Chinook\n        Examples: Sierra Nevada Porter", global: true, user_id: 1, aromas: "green, wood, ginger"},
  {name: "Olympic", origin: "U.S.", alpha: 11.5, hop_type: "Bittering", beta: 5.5, description: "Olympic is a high alpha hops released by Washington Agricultural Expt Station in 1983.  Heritage is from Brewers Gold and Fuggles.\n            Aroma: Spicy, citrus aroma\n            Substitutes: Brewers Gold", global: true, user_id: 1, aromas: "citrus, spicy"},
  {name: "Opal", origin: "Germany", alpha: 6.5, hop_type: "Aroma", beta: 4.5, description: "New variety from the hop research center in Hull.\n            Used for: Aroma hops in continental styles\n            Aroma: High aroma hops with hints of spice and subtle citrus.\n            Substitutes: Unknown", global: true, user_id: 1, aromas: "sweet spice, pepper, clean citrus, anise"},
  {name: "Orion", origin: "Germany", alpha: 7.25, hop_type: "Both", beta: 3.7, description: "Cross between Perle and an experimental variety\n            Used for: Bittering and aroma\n            Aroma: Pleasant, hoppy\n            Substitutes: Perle", global: true, user_id: 1, aromas: "spicy"},
  {name: "Pacific Jade", origin: "New Zealand", alpha: 13.0, hop_type: "Bittering", beta: 7.5, description: "High alpha bittering hops that delivers a bold herbal infusion of fresh citrus with some black pepper flavor.  Citrus aroma and flavor tempers ales and may be used as a finishing hops.\n            Used for: Ales, lagers\n            Substitutes: N/A", global: true, user_id: 1, aromas: "fresh citrus, black pepper"},
  {name: "Pacifica", origin: "New Zealand", alpha: 5.5, hop_type: "Aroma", beta: 6.0, description: "Aroma hop developed from Hallertauer Mittelfrueh in New Zealand (1994).  Blend of old and new world taste including Orange marmalade and citrus aroma notes when used in late addition.  Soft finish if used in bittering.\n            Substitutes: Chinook, Hallertauer Mittelfrueh", global: true, user_id: 1, aromas: "orange zest, marmalade"},
  {name: "Rubin", origin: "Czech Republic", alpha: 12.5, hop_type: "Bittering", beta: 4.5, description: "The Czechs classify this as a bittering hop, but with its Saaz background it is genetically similar to European aroma varieties. Bitterness is not as smooth as Saaz, and it lingers longer.\n            Used for: Ales, Lagers\n            Aroma: Similar to old middle European aroma hops.\n            Substitutions: Saaz (CZ)\n            Storage: Unknown\n            9-14% AA / 3-6% Beta", global: true, user_id: 1, aromas: nil},
  {name: "Satus", origin: "U.S.", alpha: 13.25, hop_type: "Bittering", beta: 8.75, description: "High alpha bittering hops from Yakima that is similar to Galena.  Of recent origin.\n            Substitutes: Galena", global: true, user_id: 1, aromas: nil},
  {name: "Sonnet Golding", origin: "U.S.", alpha: 4.8, hop_type: "Aroma", beta: 2.0, description: "Sonnet is a new hop variety bred from a Saaz variant that exhibited Goldings-like characteristics. It&#39;s used for aroma due to its low alpha acid content, and offers lots of floral notes.\n\n            Used for: English Ales, Belgian Style Ales\n            Substitutions: Saaz,East Kent Golding and Crystal.\n            Storage: Unknown", global: true, user_id: 1, aromas: nil},
  {name: "Perle, U.S.", origin: "U.S.", alpha: 8.25, hop_type: "Both", beta: 4.5, description: "Bred from English Northern Brewer. Slightly spicy with herbal and floral characteristics.\n\n            Used for: Stout\n            Substitutions: Northern Brewer, German Northern Brewer, German Perle", global: true, user_id: 1, aromas: "spicy, floral"},
  {name: "Phoenix", origin: "United Kingdom", alpha: 8.0, hop_type: "Bittering", beta: 4.6, description: "Seedling derived from Wye Yoeman.\n            Used for: Ales\n            Aroma: Similar to Wye Challenger hops\n            Substitutes: Challenger, East Kent Golding, Northdown", global: true, user_id: 1, aromas: "spicy, floral, chocolate"},
  {name: "Pilgrim", origin: "United Kingdom", alpha: 11.5, hop_type: "Bittering", beta: 4.7, description: "New variety from Wye College in 2001.  High Selinene content.\n            Used for: Bittering\n            Substitutes: N/A", global: true, user_id: 1, aromas: "lemon, grapefruit, pear, berry"},
  {name: "Pilot", origin: "United Kingdom", alpha: 11.5, hop_type: "Bittering", beta: 3.0, description: "A very new hops introduced in 2002 by Charles Faram &amp; Co Ltd.  Mild herbal flavor with lemon hints and fresh mowed grass flavor.\n            Used for: Primarily for bittering in APAs.\n            Rare.", global: true, user_id: 1, aromas: "marmalade, lemon, spice"},
  {name: "Pioneer", origin: "United Kingdom", alpha: 9.0, hop_type: "Both", beta: 3.75, description: "Bred from Wye Omega at Wye College in the UK\n            Used for: ESB, English Ales\n            Aroma: Mild English hop aroma\n            Substitutes: East Kent Goldings", global: true, user_id: 1, aromas: "lemon, grapefruit, cedar, herbal"},
  {name: "Premiant", origin: "Czech Republic", alpha: 7.5, hop_type: "Both", beta: 4.5, description: "A cross between Czech aroma and bittering varieties.  Dual purpose though its aroma is not as strong as some other Czech hops.  Use in beers where a moderate hoppy aroma is needed.\n            Used for: Moderate ales, lagers\n            Substitutes: Saaz", global: true, user_id: 1, aromas: "pleasant, mild"},
  {name: "Pride of Ringwood", origin: "Australia", alpha: 9.0, hop_type: "Bittering", beta: 5.75, description: "Used for: General purpose bittering hops for Australian beers\n            Aroma: Moderate citric aroma, clean bittering flavor\n            Substitutes: Cluster, Galena", global: true, user_id: 1, aromas: "cedar, oak"},
  {name: "Progress", origin: "United Kingdom", alpha: 6.25, hop_type: "Aroma", beta: 2.1, description: "Used for: Bittering and aroma in English Ales\n            Aroma: Similar to Fuggles, aromatic, slightly sweet\n            Substitutes: Fuggles", global: true, user_id: 1, aromas: "grass, mint, sweet, honey, blackcurrant"},
  {name: "Rakau (Alpharoma)", origin: "New Zealand", alpha: 10.5, hop_type: "Both", beta: 5.2, description: "Previously known as Alpharoma. Suited for new world styles where brash fruit character and big but well constructed bitterness is desired. Has been compared to Nelson Sauvin as a &quot;flavor hop&quot;.\n            Used for: Pale Ale, IPA, Lager\n            Aroma: Tropical fruit, passion fruit, apricot/peach\n            Substitutions: Amarillo, Summit\n            Storage: Good\n            10-11% AA / 4.6-6% Beta", global: true, user_id: 1, aromas: "stone fruit, fig, apricot, resin, pine needle"},
  {name: "Riwaka", origin: "New Zealand", alpha: 5.25, hop_type: "Aroma", beta: 4.5, description: "Formerly called B-Saaz, this was developed from old line Saazer hops in New Zealand.\n            Used for: New world pale ale, regional pilsners\n            Aroma: Strong, hoppy. Some grapefruit and citrus character.", global: true, user_id: 1, aromas: "grapefruit, citrus"},
  {name: "Saaz", origin: "U.S.", alpha: 3.75, hop_type: "Aroma", beta: 3.75, description: "This traditional variety from the Czech Republic is grown on a limited basis in the U.S. A classic noble aroma that is famously used in Pilsners.\n            Used for: Bohemian-style beers, Continental lagers, Wheats, Pilsener lagers, Ales, Pilsners\n            Aroma: Seasoned herbal character\n            Substitiutions: Czech Saaz, Polish Lublin, Sterling\n            Storage: Poor\n            2-6% AA / 3-4.5% Beta", global: true, user_id: 1, aromas: "earthy, spicy"},
  {name: "Santiam", origin: "U.S.", alpha: 6.0, hop_type: "Aroma", beta: 7.0, description: "Triploid aroma selection derived from Tettnang and Hallertauer (1997)\n            Used for: Lager, U.S. Pale Ale, Pilsner\n            Substitutes: German Tettnang, Spalt, Spalt Select", global: true, user_id: 1, aromas: "black pepper, spicy, floral"},
  {name: "Saphir", origin: "Germany", alpha: 3.5, hop_type: "Aroma", beta: 4.6, description: "A new noble breed as an alternative to Hallertau Mittelfrueh in an attempt to make it more disease resistant and commercially viable.  Similar in character to Hallertau varieties.\n            Used for: Pilsners, lagers, Belgian whites.\n            Substitutes: Hallertau Mittelfrueh", global: true, user_id: 1, aromas: "spicy, floral"},
  {name: "Select Spalt", origin: "Germany", alpha: 4.75, hop_type: "Aroma", beta: 3.5, description: "Bred from Hallertauer Mittelfrueh and Spalt in Germany.  Spalt character.\n            Used for: Lager, Pilsner, Noble aroma hop substitute\n            Aroma: Very fine, spalt aroma\n            Substitutes: Spalter, Saaz, Tettnang", global: true, user_id: 1, aromas: "spicy, grass"},
  {name: "Simcoe", origin: "U.S.", alpha: 13.0, hop_type: "Bittering", beta: 4.5, description: "High alpha bittering hops with good aroma characteristics.\n            Used for: IPAs\n            Aroma: Citrus and pine-like aroma\n            Substitutes: N/A", global: true, user_id: 1, aromas: "passion fruit, berry, pine, earth, citrus"},
  {name: "Sladek", origin: "Czech Republic", alpha: 5.0, hop_type: "Aroma", beta: 7.5, description: "Hybrid variety of Saaz origin with noble hoppy taste and aroma.  Generally recommended for middle-hopping about 20 min before end of boil.  Not a replacement for Saaz, but complimentary in Lagers.\n            Used for: Lager\n            Substitutes: Saaz", global: true, user_id: 1, aromas: "peach, grapefruit, passion fruit"},
  {name: "Smaragd (Emerald)", origin: "Germany", alpha: 5.0, hop_type: "Bittering", beta: 4.5, description: "Perviously known as Emerald. A fine aroma variety with high bittering value.\n            Used for: Alt, Kolsch, or blond ale.\n            Aroma: Fine aroma, floral, fruity, spicy\n            Substitutions: Unknown\n            Storage: Fair\n            4-6% AA / 3.5-5.5% Beta", global: true, user_id: 1, aromas: "anise, tobacco, clove"},
  {name: "Spalter", origin: "Germany", alpha: 4.5, hop_type: "Aroma", beta: 4.0, description: "Used for: Traditional German bittering and aroma hops, Altbiers, Lagers\n            Aroma: Mild, pleasant, slight spice\n            Substitutes: Saaz, Tettnang\n            Examples: Dusseldorf Altbiers", global: true, user_id: 1, aromas: nil},
  {name: "Spalter Select", origin: "Germany", alpha: 4.75, hop_type: "Aroma", beta: 3.5, description: "Bred from Hallertauer Mittelfrueh and Spalt in Germany.  Solid replacement for Sazzer-type hops.\n            Used for: Lager, Pilsner\n            Aroma: Spicy, floral, and woody.\n            Substitutes: Spalt Spalter, Saaz, Tettnanger\n            Storage: Good/Fair\n            3-6.5% AA / 2.5-5% Beta", global: true, user_id: 1, aromas: nil},
  {name: "Styrian Aurora (Super Styrian)", origin: "Austria", alpha: 7.5, hop_type: "Aroma", beta: 3.3, description: "Typical alpha: 7.0-9.0%. Moderate-alpha with powerful hoppy aroma, very versatile - try in US, Belgian, or English ales.\n            Uses: All Ales, some lagers\n            Aroma: Spicy, yet pleasant hoppiness\n            Substitutes: Styrian Golding, Styrian Bobek\n            Storage: Excellent\n            6.5-9.5% AA / 2.7-5.5% Beta", global: true, user_id: 1, aromas: nil},
  {name: "Styrian Bobek", origin: "Austria", alpha: 5.0, hop_type: "Bittering", beta: 2.0, description: "Derived from a Northern Brewer and Yugoslavian male TG. Low to moderate alpha and earthy, slightly fruity aroma.\n            Uses: English Style Ales, ESB, Lager, Pilsner &amp; Belgian-style Ales\n            Aroma: Intense and pleasant hoppy aroma\n            Substitutions: Styrian Golding, UK or US Fuggle, Willamette\n            Storage: Good\n            3.5-7% AA / 4-6.1% Beta", global: true, user_id: 1, aromas: nil},
  {name: "Styrian Celeja", origin: "Austria", alpha: 4.5, hop_type: "Aroma", beta: 2.0, description: "Hybrid cross between Styrian Golding, Styrian Aurora, and Slovenian wild hops. Aroma is orange marmalade like Styrian Golding, but with a noble component.\n            Used for: UK and Belgian ales or lagers.\n            Aroma: Floral, fruit\n            Substitutions: Unknown (Styrian Golding?)\n            Storage: Unknown\n            3-6% AA / ??% Beta", global: true, user_id: 1, aromas: nil},
  {name: "Styrian Goldings", origin: "Slovenia", alpha: 5.4, hop_type: "Aroma", beta: 2.9, description: "A seedless version of Fuggles grown in Slovenia\n            Used for: Bittering, finishing for a wide variety of European Beers. Popular in Europe and the United Kingdom\n            Aroma: Mild, soft, grassy, floral aroma. Similar to Fuggles.\n            Substitutes: Fuggles, Williamette", global: true, user_id: 1, aromas: nil},
  {name: "Sun", origin: "U.S.", alpha: 14.0, hop_type: "Bittering", beta: 5.5, description: "A very high alpha bittering hops from Yakima Valley, Washington\n            Used for: Bittering (intense)\n            Aroma: Pleasant, hoppy\n            Substitutes: Other very high alpha US hops", global: true, user_id: 1, aromas: nil},
  {name: "Super Alpha", origin: "New Zealand", alpha: 13.0, hop_type: "Bittering", beta: 8.0, description: "High alpha hops released in 1976 by DSIR. European in character - clean crisp flavor and resin aroma.\n            Used for: European Styles, General purpose bittering\n            Aroma: Pleasant, resin like aroma\n            Substitutes: N/A", global: true, user_id: 1, aromas: nil},
  {name: "Super Galena", origin: "U.S.", alpha: 14.5, hop_type: "Bittering", beta: 9.0, description: "Super high alpha hops from Hopsteiner breeding program released in 2006.  High alpha and beta content - comparable to Galena in bitterness and aroma profile.\n            Used for: Primarily for bitterness.\n            Substitutes: Galena", global: true, user_id: 1, aromas: nil},
  {name: "Southern Cross", origin: "New Zealand", alpha: 13.0, hop_type: "Both", beta: 6.2, description: "Dual purpose with &quot;European&quot; flavor - from DSIR in 1994\n            Used for: European flavor bittering/aroma\n            Aroma: Spicy, lemony, slight pine and wood flavors\n            Substitutes: N/A", global: true, user_id: 1, aromas: "lemon peel, lime, tropical fruit, pine"},
  {name: "Spalt Spalter", origin: "Germany", alpha: 3.0, hop_type: "Aroma", beta: 4.0, description: "Available only from the Spalt region. fire aroma is unique despite genetic sililarities to Saaz, Tettnanger.\n            Used for: Traditional German beers, Altbiers, Lagers\n            Aroma: Fine/spicy, delicate, herbal, woody, floral\n            Substitutes: Saaz, Tettnanger\n            Storage: Poor\n            2.5-3.5 AA / 3-5% Beta", global: true, user_id: 1, aromas: "noble"},
  {name: "Sterling", origin: "U.S.", alpha: 7.5, hop_type: "Both", beta: 5.0, description: "Flavor is a cross between Saaz and Mt Hood\n            Used for: Lagers, Ales, Pilsners\n            Aroma: Herbal, spicy, some floral and citrus hints\n            Substitutes: Saaz, Polish Lublin", global: true, user_id: 1, aromas: "noble, herbal, spicy, floral"},
  {name: "Sticklebract", origin: "New Zealand", alpha: 13.5, hop_type: "Both", beta: 8.0, description: "High alpha hops from DSIR research first released in 1972.  Some of the same qualities as Northern Brewer\n            Used for: Dual purpose\n            Aroma: Pine-like with a hint of citrus\n            Substitutes: Northern Brewer", global: true, user_id: 1, aromas: "citrus, pine"},
  {name: "Strisslespalt", origin: "France", alpha: 4.0, hop_type: "Aroma", beta: 3.8, description: "Aroma hops used widely in the Alsace area of France.\n            Used for: Pilsner, lager, wheat\n            Aroma: Medium intensity, pleasant, hoppy\n            Substitutes: Mt Hood, Crystal, Hallertauer Hersbruck", global: true, user_id: 1, aromas: "herbal, grass floral, spicy, grapefruit"},
  {name: "Styrian Golding (Savinja Golding)", origin: "Slovenia", alpha: 5.25, hop_type: "Aroma", beta: 2.9, description: "A seedless version of Fuggle grown in Slovenia. Popular in Europe and the United Kingdom.\n            Used for: All Ales and Lagers\n            Aroma: Delicate, slightly spicy, soft, and floral aroma. Similar to Fuggle.\n            Substitutes: Fuggle, Williamette, EKG\n            Storage: Very Good\n            4.5-6% AA / 2-3.5% Beta", global: true, user_id: 1, aromas: "noble"},
  {name: "Summer (Summer Saaz)", origin: "Australia", alpha: 5.5, hop_type: "Aroma", beta: 5.4, description: "Apricot and melon characters are more apparent when dry hopping.\n            Used for: Ales\n            Aroma: Subtle, light apricot and melon flavors\n            Substitiutions: Unknown\n            Storage: Unknown\n            4-7.5% AA / 4.8-6.7% Beta", global: true, user_id: 1, aromas: "apricot, melon"},
  {name: "Summit", origin: "U.S.", alpha: 17.0, hop_type: "Bittering", beta: 5.0, description: "High alpha variety bred by American Dwarf Hop Assoc.  Can be grown commercially on a low trellis.\n            Aroma: Strong citrus, grapefruit notes.\n            Substitutes: Warrior, Millenium, Columbus, Tomahawk, Zeus", global: true, user_id: 1, aromas: "pepper, incense, anise, orange, pink grapefruit, tangerine"},
  {name: "Super Pride", origin: "Australia", alpha: 14.25, hop_type: "Bittering", beta: 6.7, description: "A high alpha variation of the Pride of Ringwood hop variety.  Features mild aromas, higher alpha content and low cohumulone levels.\n            Used for: Primarily for bittering.\n            Substitutes: Pride of Ringwood", global: true, user_id: 1, aromas: "resin, fruit"},
  {name: "Sussex", origin: "United Kingdom", alpha: 5.0, hop_type: "Aroma", beta: 2.7, description: "Aroma similar to Fuggle. Good, yet delicate, flavor retention. Pedigree is &quot;a chance find&quot;.\n\n        Used for: Ale\n        Sustitution: N/A\n        Storage: Unknown", global: true, user_id: 1, aromas: "earthy, grass, mint, citrus, vanilla"},
  {name: "Tettnang, U.S.", origin: "U.S.", alpha: 4.5, hop_type: "Aroma", beta: 3.7, description: "Tettnang is a traditional German land-race variety known for\n            its noble aroma that is pleasant and slightly spicy. It remains well established in the Tettnang growing region of Germany, and is also grown in Oregon and Washington. Americangrown Tettnang is reported to have slightly higher myrcene levels than its German counterpart.\n\n            Used for: American style ale\n            Substitutions: German Tettnang, Santiam, German Spalt, Spalt Select", global: true, user_id: 1, aromas: nil},
  {name: "Tillicum", origin: "U.S.", alpha: 13.25, hop_type: "Bittering", beta: 9.9, description: "High alpha and high beta hops developed though Haas breeding program and released in 1995.  Daughter variety of Galena and related to Chelan - so similar to both.\n            Substitutes: Galena, Chelan", global: true, user_id: 1, aromas: nil},
  {name: "Tsingtao Flower", origin: "China", alpha: 7.0, hop_type: "Bittering", beta: 3.6, description: "Accounts for about 65% of acreage in China, the Chinese version of American Cluster.\n            Used for: Unknown (Lager?)\n            Aroma: Floral and Spicy\n            Substitutions: Unknown\n            Storage: Good\n            6-8% AA / 3-4.2% Beta", global: true, user_id: 1, aromas: nil},
  {name: "Victoria", origin: "Australia", alpha: 13.0, hop_type: "Bittering", beta: 6.25, description: "Hi alpha hops grown in Victoria, Australia.  It was created in 1976 and entered commercial production in the 1990s.\n            Used for: Primarily for bittering due to high alpha content.", global: true, user_id: 1, aromas: nil},
  {name: "Tahoma", origin: "U.S.", alpha: 7.8, hop_type: "Aroma", beta: 9.0, description: "Citrus and lemon notes with cedar, pine, floral, pepper and subtle green melon notes. Daughter of Glacier that retains the very low cohumulone characteristic of Glacier with somewhat higher alpha acid content.", global: true, user_id: 1, aromas: "lemon, grapefruit, cedar, pine, green melon, floral, pepper"},
  {name: "Target", origin: "United Kingdom", alpha: 11.0, hop_type: "Bittering", beta: 5.0, description: "Used for: English ales and lagers\n            Aroma: Robust aroma and flavor\n            Substitutes: East Kent Goldings", global: true, user_id: 1, aromas: "sage, pepper, citrus, marmalade, tangerine"},
  {name: "Tettnang", origin: "Germany", alpha: 4.5, hop_type: "Aroma", beta: 3.5, description: "Used for: German ales, lagers and wheat beer\n            Aroma: Noble, mild, fine, slightly spicy\n            Substitutes: Saaz, Spalt\n            Examples: Sam Adams Octoberfest, Anderson Valley ESB", global: true, user_id: 1, aromas: "spicy, pepper, black tea"},
  {name: "Tettnang (Tettnang Tettnager)", origin: "Germany", alpha: 4.0, hop_type: "Aroma", beta: 4.0, description: "One of the four noble hops along with Saaz, Spalt, and Hallertau Mittelfrueh, a group of mainland European hops that are prized for their similar, delicate flavor and aroma profile.\n            Used for: German ales, lagers, wheat\n            Aroma: Noble, Mild and pleasant with balanced earthy, herbal and floral aroma impressions\n            Substitutes: Saaz, Spalt, Hallertau Mittelfrueh\n            Storage: Poor\n            2.5-6% AA / 3-5% Beta", global: true, user_id: 1, aromas: "noble"},
  {name: "Tomahawk", origin: "U.S.", alpha: 15.5, hop_type: "Bittering", beta: 4.5, description: "Part of the Columbus/Tomahak/Zeus (CTZ) group of super high alpha hops.  Together these make up 1/4 of US hop acreage.\n            Used for: High alpha hops used primarily for commercial bittering.\n            Substitutes: Tomahawk, Zeus, Nugget, Galena, Chinook", global: true, user_id: 1, aromas: "pungent, black pepper, licorice, curry"},
  {name: "Topaz", origin: "Australia", alpha: 17.0, hop_type: "Bittering", beta: 6.5, description: "Very high alpha hop variety from Australia.  Used for commercial production primarily for its high alpha acid content and high bitterness.", global: true, user_id: 1, aromas: "resin, grass, lychee"},
  {name: "Tradition", origin: "Germany", alpha: 6.0, hop_type: "Bittering", beta: 4.5, description: "Close descendant of Hallertauer Mittelfrueh bred for high yield.\n            Used for: Lager, Pilsner, Bock, some Weizens\n            Aroma: Fine - similar to Hallertauer Mittelfrueh\n            Substitutes: Hallertauer Mittelfrueh, Liberty, Ultra", global: true, user_id: 1, aromas: "grass, tea, lavendar, orange"},
  {name: "Triple Pearl", origin: "U.S.", alpha: 10.7, hop_type: "Aroma", beta: 3.8, description: "Pleasant and balanced with notes of melon, orange citrus, resin, spice and pepper. Stoarge stability unknown. Released in 2013 by USDA-ARS.\n\n            Used for: Pale Ale\n            Substitutions: Perle", global: true, user_id: 1, aromas: "melon, orange, resin, spice, pepper"},
  {name: "Triskel", origin: "France", alpha: 2.6, hop_type: "Aroma", beta: 6.7, description: "A cross between Strisselspalt and the male plant of the English hop variety of Yeoman. Contains many of the same characteristics as Strisselspalt, but with a more pronounced flavor profile. Strong floral, fruit and citrus characteristics.\n\n            Used for: Belgian style ale, Lager, IPA, Pilsner, Wheat\n            Substitutions: Ahtanum, Centennial, Chinook, Simcoe\n            Storage: Unknown", global: true, user_id: 1, aromas: "floral, herbal, citrus, spicy"},
  {name: "Ultra", origin: "U.S.", alpha: 3.0, hop_type: "Aroma", beta: 3.75, description: "Triploid seedling of the German Hallertauer Variety\n            Used for: Lager, Pilsner, wheat, finishing hops in ales\n            Aroma: Good to outstanding - some Saaz like qualities\n            Substitutes: Liberty, Hallertauer, Tradition, Saaz", global: true, user_id: 1, aromas: "mild, floral, bouquet"},
  {name: "Vanguard", origin: "U.S.", alpha: 5.5, hop_type: "Aroma", beta: 6.0, description: "Diploid seedling made in 1982, released in 1997. Hallertau Mitterfrueh parentage.\n            Used for: European lagers, ales\n            Aroma: Strong aromatic notes\n            Substitutes: Hallertauer Mittelfrueh", global: true, user_id: 1, aromas: "wood"},
  {name: "Wai-iti", origin: "New Zealand", alpha: 3.0, hop_type: "Aroma", beta: 5.0, description: "Strong citrus character of lime zest with fresh peaches and apricot. Used in both ales and lagers. Good for late additions and dry hopping.\n\n            Used for: Ale, Pale Ale, IPA.\n            Substitutions: N/A", global: true, user_id: 1, aromas: "peach, apricot, spice, lime, tropical fruit"},
  {name: "Waimea", origin: "New Zealand", alpha: 17.5, hop_type: "Both", beta: 8.0, description: "A high alpha acid, dual purpose hop with lots of pine and citrus characters.\n\n            Used for: IPA\n            Substitutions: N/A", global: true, user_id: 1, aromas: "citrus, pine, herbal"},
  {name: "Wakatu (Hallertau Aroma)", origin: "New Zealand", alpha: 7.5, hop_type: "Both", beta: 8.5, description: "Formerly called Hallertau Aroma. Similar to German Hallertau but higher alpha. Excellent Dual Purpose hop, rounded floral aroma with hints of lime.\n            Used for: Lager, Pils, Bock, Wheat, Kolsch, Helles, Belgian Ales.\n            Aroma: Restrained floral notes and freshly zested lime\n            Substitutions: US Hallertau, Liberty\n            Storage: Good\n            6.5-8.5% AA / 8-9% Beta", global: true, user_id: 1, aromas: "lime zest, floral"},
  {name: "Willamette, New Zealand", origin: "New Zealand", alpha: 5.0, hop_type: "Aroma", beta: 3.8, description: "Mild, pleasant, slight spicy floral tones.\n\n            Used for: American and English Ales\n            Substitutions: US Willamette, Fuggles", global: true, user_id: 1, aromas: nil},
  {name: "Wye Challenger", origin: "New Zealand", alpha: 8.9, hop_type: "Both", beta: 5.8, description: "Spicy, clean, citrus aroma with distinct herbal notes.\n\n            Used for: English Ales, English IPA, Red Ale, Bitter, Stout, Porter\n            Substitutions: N/A", global: true, user_id: 1, aromas: nil},
  {name: "Brewer's Gold", origin: "U.S.", alpha: 8.5, hop_type: "Bittering", beta: 4.0, description: "Primarily a bittering hop and commonly used in lighter style ales, although it works well in lagers.\n            Used for: English ales, German styles, farmhouse ales.\n            Aroma: Bittering hop with neutral aroma character, spice noted to its flavor and aroma\n            Substitutes: Northdown, Northern Brewer (Germany), Galena, Bullion, Chinnok, Cascade (U.S.)\n            Storage: Fair (60-70% AA after 6 months at 20 C)\n            7-10% AA / 3.5-4.5% Beta", global: true, user_id: 1, aromas: nil},
  {name: "Fuggles", origin: "United Kingdom", alpha: 4.5, hop_type: "Aroma", beta: 2.0, description: "Used for: General purpose bittering/aroma for English Ales, Dark Lagers\n            Aroma: Mild, soft, grassy, floral aroma\n            Substitute: East Kent Goldings, Williamette\n            Examples: Samuel Smith's Pale Ale, Old Peculiar, Thomas Hardy's Ale", global: true, user_id: 1, aromas: nil},
  {name: "Admiral", origin: "United Kingdom", alpha: 14.75, hop_type: "Bittering", beta: 5.6, description: "Bittering hops derived from Wye Challenger.  Good high-alpha bittering hops.\n            Used for: Ales\n            Aroma: Primarily for bittering\n            Substitutes: Target, Northdown, Challenger", global: true, user_id: 1, aromas: "orange, tea, fresh, sap"},
  {name: "Amarillo", origin: "U.S.", alpha: 9.2, hop_type: "Aroma", beta: 6.0, description: "Discovered and introduced by Vigil Gamache Farms Inc. in Washington State. Character similar to Cascade.  Often used as a late kettle or dry hop addition to American style Pale Ales and IPA&rsquo;s due to its signature aroma characteristics. Used for both bitterness and aroma.\n            Used for: IPAs, Ales\n            Aroma: Intensely fruity (citrus, melon, and stone friuts), floral, tropical notes.\n            Substitutes: Cascade, Centennial, Simcoe\n            Storage: Good\n            8-11% AA / 6-7% Beta", global: true, user_id: 1, aromas: "grapefruit, orange, lemon, melon, apricot, peach"},
  {name: "Columbus (Tomahawk)", origin: "U.S.", alpha: 14.0, hop_type: "Bittering", beta: 5.0, description: "Engineered Centennial Substitute - High alpha bittering hops.\n            Used for: Bittering, flavor, aroma - IPA, American Pale Ale, Stout, Lager\n            Aroma: Pungent, spicy. Strongly aromatic, but clean tasting bitterness\n            Substitutes: Centennial", global: true, user_id: 1, aromas: "black pepper, licorice, citrus"},
  {name: "East Kent Goldings (EKG)", origin: "United Kingdom", alpha: 5.0, hop_type: "Both", beta: 3.5, description: "Also known as Canterbury Hops by some, although others will dispute this fact. East Kent Goldings gracefully defines the English Pale Ales and Ales produced by the region, it is quintessentially English.\n            Used for: English Ale, Porter, Scottish and Irish Ale, Stout, Strong Ale, ESB, Saison, Barleywine.\n            Aroma: Floral, aromatic, earthy, slightly sweet spicy flavor\n            Substitutes: First Gold, Fuggles, other Golding varieties, Progress, Target, and Whitbread Golding\n            Storage: Good/Fair 965-80% AA after 6 months at 20 C)\n            4-7% AA / 1.9-2.8% Beta", global: true, user_id: 1, aromas: "lavendar, honey, lemon, thyme, orange, grapefruit"},
  {name: "Green Bullet", origin: "New Zealand", alpha: 13.5, hop_type: "Bittering", beta: 7.0, description: "Bittering hops from New Zealand developed in 1972\n            Used for: Australian-style ales and lagers.\n            Aroma: Floral, raisin aroma.\n            Substitutes: Styrian Goldings", global: true, user_id: 1, aromas: "spicy, dried fruit, floral"},
  {name: "Warrior", origin: "U.S.", alpha: 15.0, hop_type: "Both", beta: 4.75, description: "High alpha hops from Yakima Chief Ranches. Also called YCR-5.  Mild aroma and low cohumulone content - primarily for bittering.\n            Used for: Ales, stouts\n            Aroma: Neutral, clean, mild\n            Substitutes: Nugget, Columbus, Magnum", global: true, user_id: 1, aromas: "mild, resinous"},
  {name: "Willamette", origin: "U.S.", alpha: 5.5, hop_type: "Aroma", beta: 3.5, description: "Used for: Finishing American and British Ales\n            Aroma: Mild, grassy, floral, slightly spicy\n            Substitutes: Fuggles, East Kent Goldings\n            Examples: Sierra Nevada Porter, Ballard Bitter", global: true, user_id: 1, aromas: "floral, incense, elderberry"},
  {name: "Yakima Gold", origin: "U.S.", alpha: 9.6, hop_type: "Both", beta: 4.7, description: "Aroma is mild and pleasant. An excellent general purpose variety with smooth bitterness and a pleasant aroma. Released by Washington State University in 2013. ", global: true, user_id: 1, aromas: "grapefruit, lemon zest"},
  {name: "Zeus", origin: "U.S.", alpha: 14.0, hop_type: "Bittering", beta: 5.0, description: "Super high alpha bittering hops developed in the Yakima Valley, WA\n            Used for: Bittering, intense aroma\n            Aroma: Aromatic, pleasant\n            Substitutes: Other high alpha hops", global: true, user_id: 1, aromas: "pungent, black pepper, licorice, curry"},
  {name: "Zythos", origin: "U.S.", alpha: 10.9, hop_type: "Bittering", beta: 5.2, description: "A proprietary pellet blend. Designed to complement existing IPA and Pale Ale hop varieties.\n            Used for: American-style Ale\n            Aroma: Distinct tropical (pineapple) and citrus tones, with slight pine characteristics\n            Substitutions: Unknown\n            Storage: Unknown\n            10-12.5% AA / 4.7-6.2% Beta", global: true, user_id: 1, aromas: "pineapple, citrus, pine"},
  {name: "Falconer's Flight 7C's Blend", origin: "U.S.", alpha: 9.5, hop_type: "Both", beta: 4.4, description: "A proprietary hop blend from HopUnion that includes 7 of the \"C\" hops along with a few experimental varieties.\n            Used for: Pale Ale, IPA, Experimental Ales\n            Aroma: Citrus/grapefruit character alongside fruity, spicy, and earthy notes\n            Substitutions: Unknown\n            Storage: Unknown\n            9-10% AA / 4.5% Beta", global: true, user_id: 1, aromas: "lemon, grapefruit"},
  {name: "Sorachi Ace", origin: "Japan", alpha: 13.0, hop_type: "Bittering", beta: 6.5, description: "Developed by Sapporo Breweries and used in their beers - has a Brewer's gold and Saaz heritage. Works well with Citra and Simcoe.\n            Used for: Saison, IPA, Pale Ale, Wheat, or other beer where lemon/citrus is desirable.\n            Aroma: Striking lemon charachter, a bold hop for bold beers.\n            Substitutes: Admiral, Cascade\n            Storage: Unknown\n            10-16% AA / 6-7% Beta", global: true, user_id: 1, aromas: "lemon, lime, dill"},
  {name: "Sylva", origin: "Australia", alpha: 5.5, hop_type: "Aroma", beta: 4.0, description: "Formerly Southern Saaz. Displays what&#39;s considered the traditional hoppy (with cedarwood notes) and spicy character of Saaz. Mostly an option for Southern Hemisphere brewers but an interesting variation on Saaz.\n            Used for: Lagers\n            Aroma: Complex and subtle, floral and herbal characteristics\n            Substitutions: Hallertau Mittlefreuh, AU Helga\n            Storage: Unknown\n            4-7.3% AA / 3-5% Beta", global: true, user_id: 1, aromas: "floral, herbal, noble"},
  {name: "Hallertau Taurus", origin: "Germany", alpha: 14.8, hop_type: "Both", beta: 5.0, description: "Bred at the Huell Hops Institute in Germany and prided in its bittering qualities. The bittering is moderately abundant and this variety adds a pleasant aroma as well.\n            Used for: Schwawrz, Oktoberfest, Lager\n            Aroma: seasoned, flavorful, zesty character\n            Substitutions: Hallertau Magnum, German Tradition\n            Storage: Fair/Good (60%-70% AA after 6 months at 20 C)\n            12.3-17.9% AA / 4-6% Beta", global: true, user_id: 1, aromas: "blackcurrant, pepper, zesty"},
  {name: "Hersbrucker", origin: "Germany", alpha: 2.75, hop_type: "Aroma", beta: 2.0, description: "Hersbrucker Hops is similar to the noble hop varieties with its low co-humulone level.\n            Aroma: Classicly spicy, herbal, with hints of citrus and stone fruit.\n            Used for: German and Bavarian style Lagers, Pilsners, Hefeweizens, Dunkles, and Bocks\n            Substitutions: Mt. Hood, Strisselspalt, Hallertau Mittelfruh, Liberty\n            Storage: Fair (55%-65% AA after 6 months at 20 C)\n            2-5% AA / 4-6% Beta", global: true, user_id: 1, aromas: "hay, tobacco, orange"},
  {name: "Mandarina Bavaria", origin: "Germany", alpha: 8.5, hop_type: "Both", beta: 6.0, description: "One of four new varieties released to German growers in 2012. Shares characteristics of its mother, Cascade, but fruitier and more herbal.\n            Used for: American pale ale, lager (as late hop)\n            Aroma: Distinct tangerine and citrus flavors\n            Substitutions: Unknown\n            Storage: Unknown\n            7-10% AA / 4-7% Beta", global: true, user_id: 1, aromas: "tangerine, grapefruit, lime"},
  {name: "Pacific Gem", origin: "New Zealand", alpha: 15.0, hop_type: "Bittering", beta: 8.2, description: "High alpha smoothcone cross developed at DSIR in 1987.\n            Used for: Bittering - produces a cask-oak woody flavor.\n            Aroma: Pleasant - some blackberry aroma.\n            Substitutes: Bullion", global: true, user_id: 1, aromas: "oak, blackberry"},
  {name: "Perle", origin: "Germany", alpha: 8.0, hop_type: "Aroma", beta: 4.75, description: "Perle has a spciy character, and the aroma is moderate and pleasing. Commonly compared to Hallertau Mittlefruh.\n            Used for: German ales, lagers, Plisner, Alt, Maisbier, Dunkle, Kolsch\n            Aroma: Delicate floral and fruity tones\n            Substitutes: Northern Brewer, Perle (U.S.), Hallertau Mittelfruh, Hallertau Aroma\n            Storage: Good\n            6-10% AA / 2.5-5% Beta", global: true, user_id: 1, aromas: "mint, tea, pepper"},
  {name: "Polaris", origin: "Germany", alpha: 21.0, hop_type: "Bittering", beta: 6.0, description: "Very high alpha and stunninly high oil content. Its aroma variously described as &quot;ice candy&quot;, eucalyptus, peppermint, and citrus. A variety with bold taste and differentiating flavor.\n            Used for: Lagers, Ales, Pale Ales, IPA\n            Aroma: Intense floral and pleasant mint tones\n            Substitutes: Unknown\n            Storage: Unknown\n            19-23% AA / 5-7% Beta", global: true, user_id: 1, aromas: "mint, pineapple, menthol"},
  {name: "Sovereign", origin: "United Kingdom", alpha: 5.5, hop_type: "Aroma", beta: 2.6, description: "Pleasant but intense fruity flavors with mild floral, grassy, herbal and mint characteristics.\n\n            Used for: Pale Ale\n            Substitution: Fuggles\n            Storage: Unknown", global: true, user_id: 1, aromas: "floral, grass, herbal, mint, sweet, pear"},
  {name: "Vic Secret", origin: "Australia", alpha: 15.5, hop_type: "Aroma", beta: 2.6, description: "The clean, distinct fruit and pine characters of Vic Secret are best accessed by dry hopping or whirlpool additions. Late kettle additions have been found to impart a pleasant earthy character, but little fruit. Maintains very high levels of alpha acid so brewers may find it easier to craft the right balance in their beer by dry hopping. First commercial harvest in 2013.\n            Uses: Ales, Lagers, Experimental Ales\n            Aroma: Clean, distinct fruit and pine\n            Substitutions: Unknown\n            Storage: Unknown\n            14-17% AA / 2.2-2.8% Beta", global: true, user_id: 1, aromas: "pine, earth, pineapple, passionfruit"},
  {name: "Whitbread Golding Variety (WGV)", origin: "United Kingdom", alpha: 6.0, hop_type: "Aroma", beta: 2.5, description: "Derived in England in 1911 from Bates Brewer. Some Golding characteristics, but lacking the full Goldings aroma.\n            Used for: Ales\n            Aroma: Pleasant, hoppy, slightly intense\n            Substitutes: East Kent Goldings, Progress", global: true, user_id: 1, aromas: "fresh earth, floral, sweet fruit"}
