  {name: "Rye Malt", global: true, origin: "US", malt_type_id: 1, color: 4.7, malt_yield: 63.0, max_percent: 15.0, must_mash: true, protein: 10.3, diastatic_power: 75.0, description: "Adds a dry, crisp character to the beer.   Yields a deep red color, and a distinctive rye flavor Must limit to 10-15% fo the mash as it tends to produce 'stuck' mashes.", user_id: 1},
  {name: "Smoked Malt", global: true, origin: "Germany", malt_type_id: 1, color: 9.0, malt_yield: 80.0, max_percent: 100.0, must_mash: true, protein: 11.5, diastatic_power: 0.0, description: "Malt that has been smoked over an open fire. Creates a distinctive 'smoked' flavor and aroma. Used primarily for German Rauchbier but can also be added to Brown Ales and Porters.", user_id: 1},
  {name: "Special B Malt", global: true, origin: "Belgium", malt_type_id: 1, color: 180.0, malt_yield: 65.2, max_percent: 10.0, must_mash: true, protein: 0.0, diastatic_power: 0.0, description: "Extreme caramel aroma and flavored malt.  Used in dark Belgian Abbey and Trappist ales.  Unique flavor and aroma.", user_id: 1},
  {name: "Special Roast", global: true, origin: "US", malt_type_id: 1, color: 50.0, malt_yield: 72.0, max_percent: 10.0, must_mash: true, protein: 10.5, diastatic_power: 6.0, description: "Use for English ales, nut brown ales and porters. Adds a toasted, biscuit like flavor and arom.", user_id: 1},
  {name: "Toasted Malt", global: true, origin: "United Kingdom", malt_type_id: 1, color: 27.0, malt_yield: 71.7, max_percent: 10.0, must_mash: true, protein: 11.7, diastatic_power: 0.0, description: "Toasted malt can be made at home by toasting pale malted barley for 10-15 minutes at 350 F.  Similar to Biscuit or Victory malt - this malt adds reddish/orange color and improved body without sweetness.  Toasted flavor.  Mashing required to avoid haze.", user_id: 1},
  {name: "Victory Malt", global: true, origin: "US", malt_type_id: 1, color: 25.0, malt_yield: 73.0, max_percent: 15.0, must_mash: true, protein: 13.2, diastatic_power: 50.0, description: "Toasted malt that adds a 'Biscuit' or toasted flavor to English ales. Use for: Nut brown ales, porter.", user_id: 1},
  {name: "Vienna Malt", global: true, origin: "Germany", malt_type_id: 1, color: 3.5, malt_yield: 78.0, max_percent: 90.0, must_mash: true, protein: 11.0, diastatic_power: 50.0, description: "Kiln dried malt darker than Pale Malt, but not as dark as Munich Malt Imparts a golden to orange color to the beer.", user_id: 1},
  {name: "Wheat Malt, Bel", global: true, origin: "Belgium", malt_type_id: 1, color: 2.0, malt_yield: 81.0, max_percent: 60.0, must_mash: true, protein: 11.5, diastatic_power: 74.0, description: "Malted wheat for use in Wheat beer.", user_id: 1},
  {name: "Wheat Malt, Dark", global: true, origin: "Germany", malt_type_id: 1, color: 9.0, malt_yield: 84.0, max_percent: 20.0, must_mash: true, protein: 11.5, diastatic_power: 10.0, description: "Dark malted wheat base for use in dark wheat styles such as Dunkleweizen.", user_id: 1},
  {name: "Wheat Malt, Ger", global: true, origin: "Germany", malt_type_id: 1, color: 2.0, malt_yield: 84.0, max_percent: 60.0, must_mash: true, protein: 12.5, diastatic_power: 95.0, description: "Malted wheat base for use in all wheat style.", user_id: 1},
  {name: "Wheat, Roasted", global: true, origin: "Germany", malt_type_id: 1, color: 425.0, malt_yield: 54.3, max_percent: 10.0, must_mash: true, protein: 13.0, diastatic_power: 0.0, description: "Also called Chocolate Wheat Malt.  Kilned raw wheat - adds a deep, dark brown color to dunkelweizens and other dark beer styles. Roasted, woody, charcoal or burnt toast flavor depending on degree of roasting.", user_id: 1},
  {name: "White Wheat Malt", global: true, origin: "US", malt_type_id: 1, color: 2.4, malt_yield: 86.0, max_percent: 60.0, must_mash: true, protein: 14.5, diastatic_power: 130.0, description: "White wheat gives a malty flavor not available from raw wheat. Used in Weiss, Wit and White beer.", user_id: 1},
  {name: "Amber Liquid Extract", global: true, origin: "US", malt_type_id: 2, color: 12.5, malt_yield: 78.0, max_percent: 100.0, must_mash: false, protein: 11.7, diastatic_power: 120.0, description: "Unhopped amber color liquid extract. Use as a base malt for any Ale.", user_id: 1},
  {name: "Dark Liquid Extract", global: true, origin: "US", malt_type_id: 2, color: 17.5, malt_yield: 78.0, max_percent: 100.0, must_mash: false, protein: 11.7, diastatic_power: 120.0, description: "Unhopped dark liquid extract. Use as a base malt for any dark beer.", user_id: 1},
  {name: "Lyle's Golden Syrup", global: true, origin: "United Kingdom", malt_type_id: 2, color: 0.0, malt_yield: 78.3, max_percent: 20.0, must_mash: false, protein: 11.7, diastatic_power: 120.0, description: "Liquid Invert Sugar.  Used to increase alcohol and lighten the beer without alterning flavor.  Used in Belgian ales and some English ales.", user_id: 1},
  {name: "Pale Liquid Extract", global: true, origin: "US", malt_type_id: 2, color: 8.0, malt_yield: 78.0, max_percent: 100.0, must_mash: false, protein: 11.7, diastatic_power: 120.0, description: "Unhopped pale liquid extract. Use as a base malt for any ale or lager.", user_id: 1},
  {name: "Pilsner Liquid Extract", global: true, origin: "US", malt_type_id: 2, color: 3.5, malt_yield: 78.0, max_percent: 100.0, must_mash: false, protein: 11.7, diastatic_power: 120.0, description: "Unhopped pale liquid extract. Use as a base malt for any ale or lager.", user_id: 1},
  {name: "Rice Extract Syrup", global: true, origin: "US", malt_type_id: 2, color: 7.0, malt_yield: 69.6, max_percent: 15.0, must_mash: false, protein: 11.7, diastatic_power: 120.0, description: "Used like other rice adjuncts in American and Japenes lagers to provide a dry, clean taste and light body.  Adds gravity without changing body or flavor substantially.  Use in place of corn sugar in small quantities.", user_id: 1},
  {name: "Rye, Flaked", global: true, origin: "US", malt_type_id: 5, color: 2.0, malt_yield: 78.3, max_percent: 10.0, must_mash: true, protein: 0.0, diastatic_power: 0.0, description: "Imparts a dry, crisp rye flavor to rye beers.  Can be easier to mash than raw rye.", user_id: 1},
  {name: "Rice, Flaked", global: true, origin: "US", malt_type_id: 5, color: 1.0, malt_yield: 70.0, max_percent: 25.0, must_mash: true, protein: 10.0, diastatic_power: 0.0, description: "Used to add fermentables without increasing body or flavor. Produces a milder, less grainy tasting beer. Examples: American lagers, Bohemian lagers, Pilsner.", user_id: 1},
  {name: "Wheat, Torrified", global: true, origin: "US", malt_type_id: 5, color: 1.7, malt_yield: 79.0, max_percent: 40.0, must_mash: true, protein: 16.0, diastatic_power: 0.0, description: "Unmodified wheat that has been 'popped' to open the kernels Used primarily in place of raw wheat when making Belgian White and Wit Faster conversion and higher yield than raw wheat. Protein rest recommended when mashing.", user_id: 1},
  {name: "Treacle", global: true, origin: "United Kingdom", malt_type_id: 2, color: 100.0, malt_yield: 78.3, max_percent: 15.0, must_mash: false, protein: 11.7, diastatic_power: 120.0, description: "UK mixture of molasses, invert sugar and golden (corn) syrup.  Imparts an intense sweet flavor and dark color.  Used in dark English ales.  Also called 'Black Treacle' or the brand name 'Lyle's Black Treacle Syrup.'.", user_id: 1},
  {name: "Wheat Liquid Extract", global: true, origin: "US", malt_type_id: 2, color: 8.0, malt_yield: 78.0, max_percent: 100.0, must_mash: false, protein: 11.7, diastatic_power: 120.0, description: "Unhopped Wheat liquid malt extract. Contains approx 60% Wheat, 40% Barley Use for Bavarian wheats, weizens, weiss and dunkel weizen styles.", user_id: 1},
  {name: "Amber Dry Extract", global: true, origin: "US", malt_type_id: 3, color: 12.5, malt_yield: 95.0, max_percent: 100.0, must_mash: false, protein: 11.7, diastatic_power: 120.0, description: "Amber colored dry malt extract, for general purpose use. Use in any medium to dark colored beer. Examples: Ales, Marzens, Amber Ale, India Pale Al.", user_id: 1},
  {name: "Dark Dry Extract", global: true, origin: "US", malt_type_id: 3, color: 17.5, malt_yield: 95.0, max_percent: 100.0, must_mash: false, protein: 11.7, diastatic_power: 120.0, description: "Dark dry extract for general purpose use. Use as base or to increase gravity of any dark ale or lager.", user_id: 1},
  {name: "Extra Light Dry Extract", global: true, origin: "US", malt_type_id: 3, color: 3.0, malt_yield: 95.0, max_percent: 100.0, must_mash: false, protein: 11.7, diastatic_power: 120.0, description: "Very light color dry malt extract for general purpose use. Use as base or to increase gravity of any ale or lager.", user_id: 1},
  {name: "Light Dry Extract", global: true, origin: "US", malt_type_id: 3, color: 8.0, malt_yield: 95.0, max_percent: 100.0, must_mash: false, protein: 11.7, diastatic_power: 120.0, description: "Pale dry malt extract for general purpose use. Use as base or to increase gravity of any full bodied ale or lager.", user_id: 1},
  {name: "Wheat Dry Extract", global: true, origin: "US", malt_type_id: 3, color: 8.0, malt_yield: 95.0, max_percent: 100.0, must_mash: false, protein: 11.7, diastatic_power: 120.0, description: "Wheat based dry extract for use in all wheat beers. Use as base or to increase gravity of any wheat beer. Examples: Weizen, weiss, wheat ales and lager.", user_id: 1},
  {name: "Grits", global: true, origin: "US", malt_type_id: 1, color: 1.0, malt_yield: 80.0, max_percent: 10.0, must_mash: false, protein: 0.0, diastatic_power: 0.0, description: "Imparts a corn or grain taste.  Mash required. Used to increase gravity of American lagers.", user_id: 1},
  {name: "Rice Hulls", global: true, origin: "US", malt_type_id: 5, color: 0.0, malt_yield: 0.0, max_percent: 5.0, must_mash: false, protein: 11.7, diastatic_power: 120.0, description: "Hulls are introduced to improve the speed of lautering when making high gravity or high adjunct beers. Hulls are neutral in flavor, body and color, and are inert Good for wheat beers, Wits, and others that have high protein mashes.", user_id: 1},
  {name: "Barley Hulls", global: true, origin: "US", malt_type_id: 5, color: 0.0, malt_yield: 0.0, max_percent: 5.0, must_mash: false, protein: 11.7, diastatic_power: 120.0, description: "Hulls are introduced to improve the speed of lautering when making high gravity or high adjunct beers. Hulls are neutral in flavor, body and color, and are inert Good for wheat beers, Wits, and others that have high protein mashes.", user_id: 1},
  {name: "Oats, Flaked", global: true, origin: "US", malt_type_id: 5, color: 1.0, malt_yield: 80.0, max_percent: 30.0, must_mash: true, protein: 9.0, diastatic_power: 0.0, description: "Adds body, mouth feel and head retention to the beer Used in oatmeal stouts and porters Adds substantial protein haze to light beers Protein rest recommended unless flakes are pregelatinize.", user_id: 1},
  {name: "Barley, Flaked", global: true, origin: "US", malt_type_id: 5, color: 1.7, malt_yield: 70.0, max_percent: 20.0, must_mash: true, protein: 13.5, diastatic_power: 0.0, description: "Adds significant body to Porters and Stouts.  High haze producing protein prevents use in light beers.", user_id: 1},
  {name: "Barley, Raw", global: true, origin: "US", malt_type_id: 5, color: 2.0, malt_yield: 60.9, max_percent: 15.0, must_mash: true, protein: 11.7, diastatic_power: 0.0, description: "Raw, unmalted barley can be used to add body to your beer.  Use in homebrew requires very fine milling combined with a decoction or multi-stage mash.  Performs best when used in small quantities with well modified grains.", user_id: 1},
  {name: "Wheat, Flaked", global: true, origin: "US", malt_type_id: 5, color: 1.6, malt_yield: 77.0, max_percent: 40.0, must_mash: true, protein: 16.0, diastatic_power: 0.0, description: "Flaked wheat adds to increased body and foam retention Used in place of raw or torrified wheat for faster conversion and better malt yield. May be used in small amounts to improve head retention and body", user_id: 1},
  {name: "Wheat, Raw", global: true, origin: "", malt_type_id: 5, color: 2.0, malt_yield: 60.0, max_percent: 15.0, must_mash: true, protein: 11.7, diastatic_power: 0.0, description: "Raw, unmalted barley can be used to add body to your beer.  Use in homebrew requires very fine milling combined with a decoction or multi-stage mash.  Performs best when used in small quantities with well modified grains.", user_id: 1}
