MashStep.create!([
  {name: "Acid Rest", description: "First rest after dough-in. This step is meant to lower the pH of the mash and break down glucans that can gum up a mash. This step is not necessary for fully-modified malt. Try this step if you have issues w/ haze.", min_temp: 95, max_temp: 113, min_time: 15, max_time: 30},
  {name: "Peptidase Rest", description: "A protein rest, this is the optimal range for Peptidase enzyme groups. A protein rest breaks down longer chain protein, the enzyme group this rest favors breaks them into smaller length chains. The step promotes potentially greater efficiency.", min_temp: 113, max_temp: 128, min_time: 15, max_time: 30},
  {name: "Proteinase Rest", description: "A protein rest, this is the optimal range for Proteinase enzyme groups. A protein rest breaks down longer chain protein, the enzyme group this rest favors breaks them into medium length chains. The step promotes potentially greater efficiency.", min_temp: 131, max_temp: 137, min_time: 15, max_time: 30},
  {name: "Beta-Amylase Rest", description: "This rest is used to make maltier beers as it will favor beta-amylase utilization which cuts starch into maltose, a non-fermentable sugar.", min_temp: 140, max_temp: 145, min_time: 60, max_time: 120},
  {name: "Alpha- and Beta-Amylase", description: "This rest is used to making neutral beers as it will favor both alpha- and beta-amylase utilization.", min_temp: 150, max_temp: 154, min_time: 60, max_time: 120},
  {name: "Alpha-Amylase Rest", description: "This rest is used to make stronger beers as it will favor alpha-amylase utilization which cuts starch into fermentable sugar.", min_temp: 155, max_temp: 162, min_time: 20, max_time: 60},
  {name: "Mash Out", description: "Recommended for any beer that is supposed to be full-bodied as it will ensure enzyme activity is decreased.", min_temp: 168, max_temp: 172, min_time: 5, max_time: 10}
])
