Miscellaneou.create!([
  {name: "Clearfine", miscellaneous_type: "Fining", amount: 0.25, amount_label: "oz", batch_size: 5.0, use_for: "Clarity", time: 1440.0, description: "Commercial blend of animal collagen and polysaccarides that helps rapidly precipitate yeast and tannins.  Mix with 2 oz cold water and add after fermentation has completed.", usage: "Secondary", global: true, user_id: 1},
  {name: "Gelatin", miscellaneous_type: "Fining", amount: 0.5, amount_label: "tsp", batch_size: 5.0, use_for: "Clarity", time: 300.0, description: "Clear, unflavored gelatin can be purchased from your grocer.  Aids in settling yeast.  Prepare in 1 pint of water by heating gently until dissolved.  Add before bottling.  Do not boil.", usage: "Secondary", global: true, user_id: 1},
  {name: "Irish Moss", miscellaneous_type: "Fining", amount: 0.25, amount_label: "tsp", batch_size: 5.0, use_for: "Clarity", time: 10.0, description: "Fining agent that aids in the post-boil protein break.  Reduces protein chill haze and improves beer clarity.", usage: "Boil", global: true, user_id: 1},
  {name: "Isinglass (Liquid)", miscellaneous_type: "Fining", amount: 3.0, amount_label: "tsp", batch_size: 5.0, use_for: "Clarity", time: 10080.0, description: "Keep liquid form refrigerated.  Isinglass is widely used by commercial brewers to aid clarity, but is not as popular for home brewers due to stability.  Aids in settling proteins, tannins and yeast.", usage: "Secondary", global: true, user_id: 1},
  {name: "Papain", miscellaneous_type: "Fining", amount: 0.5, amount_label: "g", batch_size: 5.0, use_for: "Chill Haze", time: 5760.0, description: "An enzyme extracted from papaya.  Helps reduce protein chill haze.  Add 0.5 gram at end of secondary fermentation, allow several days for it to work.  Available from herb and spice shops.", usage: "Secondary", global: true, user_id: 1},
  {name: "Polyclar", miscellaneous_type: "Fining", amount: 0.25, amount_label: "oz", batch_size: 5.0, use_for: "Chill Haze", time: 1440.0, description: "Plastic powder that reduces chill haze by removing tannins and proteins.  Add to secondary after yeast has settled.  Amounts vary by manufacturer -- check instructions before adding. Do not boil.", usage: "Secondary", global: true, user_id: 1},
  {name: "Whirlfloc Tablet", miscellaneous_type: "Fining", amount: 1.0, amount_label: "g", batch_size: 5.0, use_for: "Clarity", time: 15.0, description: "Aids in clearing yeast and chill haze.  Easy to use tablet form.", usage: "Boil", global: true, user_id: 1},
  {name: "Apricot Extract", miscellaneous_type: "Flavor", amount: 6.5, amount_label: "oz", batch_size: 5.0, use_for: "Fruit Beer", time: 5.0, description: "Apricot flavor extract.  Mix into beer just before bottling.  Adds apricot flavor to light ales and wheat beers.  Use low hop rate to allow sweet flavor through.", usage: "Bottling", global: true, user_id: 1},
  {name: "Blackberry Extract", miscellaneous_type: "Flavor", amount: 4.5, amount_label: "oz", batch_size: 5.0, use_for: "Fruit Beer", time: 5.0, description: "Blackberry extract flavoring.  Mix into beer at bottling to add berry flavor to light ales and wheat beers.  Use low hop rate to allow sweet flavor through.", usage: "Bottling", global: true, user_id: 1},
  {name: "Cherry Extract", miscellaneous_type: "Flavor", amount: 3.5, amount_label: "oz", batch_size: 5.0, use_for: "Fruit Beer", time: 5.0, description: "Cherry extract flavoring.  Mix into beer at bottling to add cherry flavor to light ales and wheat beers.  Use low hop rate to allow sweet flavor through.", usage: "Bottling", global: true, user_id: 1},
  {name: "Cranberry Extract", miscellaneous_type: "Flavor", amount: 3.5, amount_label: "oz", batch_size: 5.0, use_for: "Fruit Beer", time: 5.0, description: "Cranberry flavor extract.  Add to beer just before bottling.  Adds distinctive cranberry flavor to light ales and wheat beers.  Use low hop rate to allow sweet flavor through.", usage: "Bottling", global: true, user_id: 1},
  {name: "Oak Chips", miscellaneous_type: "Flavor", amount: 4.0, amount_label: "oz", batch_size: 5.0, use_for: "Oak Flavor", time: 10080.0, description: "Can be added to the secondary or keg during aging to simulate oak flavor.  Boil first to sterilize and place in weighted bag so they do not float.  Amounts vary with chip size and time - smaller chips impart more flavor.", usage: "Secondary", global: true, user_id: 1},
  {name: "Oak Essence (Liquid)", miscellaneous_type: "Flavor", amount: 2.0, amount_label: "oz", batch_size: 5.0, use_for: "Oak Flavor", time: 5.0, description: "Adds Oak Aged flavor to English Ales, IPAs.  Mix in prior to bottling.  Amouts vary with concentration, so check label or add to taste.", usage: "Bottling", global: true, user_id: 1},
  {name: "Raspberry Extract", miscellaneous_type: "Flavor", amount: 6.5, amount_label: "oz", batch_size: 5.0, use_for: "Fruit Beer", time: 5.0, description: "Raspberry extract flavoring.  Mix into beer at bottling to add berry flavor to light ales and wheat beers.  Use low hop rate to allow sweet flavor through.", usage: "Bottling", global: true, user_id: 1},
  {name: "Spruce Extract", miscellaneous_type: "Flavor", amount: 1.0, amount_label: "tsp", batch_size: 5.0, use_for: "Spruce Beer", time: 5.0, description: "Spruce essence generates the flavor and smell of fresh spruce pine needles.  Add in small increments and taste as it can be a rather strong flavoring.", usage: "Bottling", global: true, user_id: 1},
  {name: "Ginger Root", miscellaneous_type: "Herb", amount: 1.0, amount_label: "oz", batch_size: 5.0, use_for: "Holiday Beer", time: 12.0, description: "Adds distinctive ginger flavor for Ginger Ales, Holiday Beers.  Fresh grated ginger root from the grocer is best.  As little as 1/2 ounce per 5 gallons is noticable, but up to 4 oz can be used for ginger ales.", usage: "Boil", global: true, user_id: 1},
  {name: "Amylase Enzyme", miscellaneous_type: "Other", amount: 0.5, amount_label: "tsp", batch_size: 5.0, use_for: "Fermentation", time: 4320.0, description: "Enhances fermentation and lowers final gravity by breaking down complex starches.  Produces light bodied, high alcohol beers.  Add when pitching yeast.", usage: "Primary", global: true, user_id: 1},
  {name: "Ascorbic Acid", miscellaneous_type: "Other", amount: 1.0, amount_label: "oz", batch_size: 5.0, use_for: "Preservative", time: 5.0, description: "This is Vitimin C.  Adding at bottling time to wine and soda as a preservative.  Not recommended for beer due to the off flavors it might add.", usage: "Bottling", global: true, user_id: 1},
  {name: "Bottle Caps", miscellaneous_type: "Other", amount: 52.0, amount_label: "g", batch_size: 5.0, use_for: "Bottles", time: 1.0, description: "Useful for bottling -- keep your inventory here.", usage: "Bottling", global: true, user_id: 1},
  {name: "Heading Liquid", miscellaneous_type: "Other", amount: 0.5, amount_label: "tsp", batch_size: 5.0, use_for: "Head Retention", time: 5.0, description: "Aids head formation and retention.  Mix into beer before bottling.", usage: "Bottling", global: true, user_id: 1},
  {name: "Yeast Nutrient", miscellaneous_type: "Other", amount: 1.0, amount_label: "tsp", batch_size: 5.0, use_for: "Fermentation", time: 4320.0, description: "Gives yeast nutrients for healthy fermentation.  Not needed for most beers, but good for those with a high percentage of adjuncts.  Add before yeast.", usage: "Primary", global: true, user_id: 1},
  {name: "Anise, Star", miscellaneous_type: "Spice", amount: 2.5, amount_label: "oz", batch_size: 5.0, use_for: "Licorice flavor", time: 30.0, description: "Star Anise spice adds a strong black licorice flavor to holiday and specialty beers.  Boil for 30 minutes to extract flavor.", usage: "Boil", global: true, user_id: 1},
  {name: "Cinnamon Stick", miscellaneous_type: "Spice", amount: 0.1, amount_label: "oz", batch_size: 5.0, use_for: "Holiday Beer", time: 5.0, description: "Used to add cinnamon flavor to holiday beers.  Goes well with ginger, honey and orange flavors.  Add one stick 5 minutes from the end of the boil.", usage: "Boil", global: true, user_id: 1},
  {name: "Coriander Seed", miscellaneous_type: "Spice", amount: 0.75, amount_label: "oz", batch_size: 5.0, use_for: "Belgian Wit", time: 5.0, description: "Used in Belgian Wit, Whites, and Holiday ales.  Very good when used in light wheat ales.  Often used with Bitter Orange Peel.", usage: "Boil", global: true, user_id: 1},
