  {name: "Old German Ale", user_id: 1, global: true, lab: "Brewtek", product_id: "CL-0400", yeast_type: "Ale", form: "Liquid", cell_count: 100.0, min_attenuation: 75.0, max_attenuation: 77.0, flocculation: 1, min_temperature: 62.0, max_temperature: 72.0, description: "Altbier, German ales, some Wheat beers.\n    Notes: Traditional Alt Bier flavor.  Strong fermenter with a smooth, attenuated, mild flavor.  Slightly dry, clean, quenching finish."},
  {name: "Roselare Belgian Blend", user_id: 1, global: true, lab: "Wyeast Labs", product_id: "3763", yeast_type: "Ale", form: "Liquid", cell_count: 100.0, min_attenuation: 78.0, max_attenuation: 82.0, flocculation: 1, min_temperature: 55.0, max_temperature: 80.0, description: "Belgian sour brown and red beers.\n    Notes: Culture of Saccharomyces, Brettonomyces and Lactic Acid Bacteria.  Complex aromas and flavors.  May be used for primary fermentation.  Primarily for sour brown and red Belgian styles."},
  {name: "Old Sonoma", user_id: 1, global: true, lab: "White Labs", product_id: "WLP076", yeast_type: "Ale", form: "Liquid", cell_count: 100.0, min_attenuation: 70.0, max_attenuation: 74.0, flocculation: 1, min_temperature: 66.0, max_temperature: 70.0, description: "American Ales, Porters, Stouts\n    Notes: Yeast from an historic brewery in Northern California, this strain was embraced by the early pioneers of craft beer in America and continues to be a great choice for those seeking to use a traditional British-style yeast. A neutral and versatile strain, it is a great choice for pale ales, porters, and stouts."},
  {name: "Ols Newark Ale", user_id: 1, global: true, lab: "East Coast Yeast", product_id: "ECY10", yeast_type: "Ale", form: "Liquid", cell_count: 100.0, min_attenuation: 75.0, max_attenuation: 77.0, flocculation: 2, min_temperature: 60.0, max_temperature: 68.0, description: "American ales, English ales\n    Notes: Sourced from a defunct east coast brewery, this pure strain was identified as their &quot;ale-pitching yeast&quot;. Good for all styles of American and English ales with high flocculation and a compact sedimentation. Suggested fermentation temperature: 60-68F; attenuation ~76%.\n  "},
  {name: "Original Pilsner", user_id: 1, global: true, lab: "Brewtek", product_id: "CL-0600", yeast_type: "Lager", form: "Liquid", cell_count: 100.0, min_attenuation: 75.0, max_attenuation: 76.0, flocculation: 1, min_temperature: 48.0, max_temperature: 58.0, description: "Classic Czech Pilsners\n    Notes: Full bodied lager yeast with sweet, underattenuated finish.  Subdued diacetyl character.  Big malty palatte.  Classic Pilsner finish and style."},
  {name: "Oud Brune", user_id: 1, global: true, lab: "East Coast Yeast", product_id: "ECY23", yeast_type: "Ale", form: "Liquid", cell_count: 100.0, min_attenuation: 68.0, max_attenuation: 76.0, flocculation: 1, min_temperature: 68.0, max_temperature: 74.0, description: "Sours\n    Notes: For primary fermentations. A unique blend of fruity Belgian Saccharomyces yeast and Lactobacilli species (L. delbrueckii and L. brevis). For those who prefer sourness without the presence of Brett. Additional info: Suggested fermentation temperature: 68-74&ordm;F"},
  {name: "Pacific Ale", user_id: 1, global: true, lab: "White Labs", product_id: "WLP041", yeast_type: "Ale", form: "Liquid", cell_count: 100.0, min_attenuation: 65.0, max_attenuation: 70.0, flocculation: 2, min_temperature: 65.0, max_temperature: 68.0, description: "English &amp; American ales including milds, bitters, IPA, porters and English stouts.\n    Notes: Popular yeast from the Pacific Northwest.  Leaves a clear and malty profile.  More fruity than WLP002.  Suitable for many English and American styles."},
  {name: "Pacific NW Ale", user_id: 1, global: true, lab: "Omega", product_id: "OYL-012", yeast_type: "Ale", form: "Liquid", cell_count: 150.0, min_attenuation: 67.0, max_attenuation: 71.0, flocculation: 2, min_temperature: 65.0, max_temperature: 75.0, description: "English &amp; American ales including milds, bitters, IPA, porters and English stouts.\n    Notes: Produces a malty and mildly fruity ale with good depth and complexity."},
